const cellPhoneRef = useMask({ mask: "(___) ___-____", replacement: { _: /\d/ } });
const workPhoneRef = useMask({ mask: "(___) ___-____", replacement: { _: /\d/ } });

const stripMask = (val) => (val ? val.replace(/\D/g, "") : ""); // âœ… remove formatting


  const [formData, setFormData] = useState({
    first_name: "",
    last_name: "",
    middle_initial: "",
    dob: "",
    age: "",
    address: "",
    city: "",
    province: "",
    postal_code: "",
    home_phone: "",
    cell_phone: "",
    work_phone: "",
    email: "",
    can_email: false,
    signing_for_minor: false,
