  background-color: #d6b263;
  color: #111;
  border: none;
  border-radius: 8px;
  padding: 12px 0;
  width: 100%;
  font-weight: 600;
  font-size: 1rem;
  cursor: pointer;
  transition: background 0.3s ease;
}

.details-btn:hover {
  background-color: #c8a64f;
}

/* #detailsModal {
  max-width: 400px;
 margin: auto;
} */



#detailsModal .modal-content {
  max-width: 400px;
  border-radius: 20px;
  border: 0;
  border-bottom: 2px solid var(--brown-btn);
}

#detailsModal .modal-content span {
  font-size: 13px;
  color: gray;
}


/* history page style start  */

.custom-search-box {
  display: flex;
  align-items: center;
  border: 1px solid #ccc;
  border-radius: 20px;
  padding: 12px 10px;
  width: 250px;
  border-radius: 10px;
  border: 1px solid var(--black);
}

.search-icon {
  font-size: 16px;
  margin-right: 8px;
  color: #555;
}

.custom-search-box input {
  border: none;
  outline: none;
  width: 100%;
  font-size: 14px;
}

.all-waiver {
  color: var(--brown-btn);
  font-weight: 600;
  border-bottom: 2px solid var(--brown-btn);
  padding: 12px 10px;
}

.confirm {
  font-weight: 500;
}

.unconfirm {
  font-weight: 500;
}

.history-table table {
  width: 100%;
  border-collapse: collapse;
  margin: 0;
}

thead th:first-child {
  border-top-left-radius: 10px;
  border-bottom-left-radius: 10px;
}

thead th:last-child {
  border-top-right-radius: 10px;
  border-bottom-right-radius: 10px;
}


.history-table th,td {
  padding: 12px 10px;
  text-align: left;
  border: 0;
  font-weight: 500;
}

.history-table th {
  background-color: #f5f5f5;
  font-weight: bold;

}

.confirmed,
.unconfirmed {
  display: inline-block;
  text-align: center;
  min-width: 120px; /* or use fixed width if you prefer */
  border-radius: 10px;
  padding: 8px 10px;
}

.history-table .confirmed {
 color: #267309;
 background-color: #E7F7E1;
}


.history-table .unconfirmed {
  background-color: #fff3cd;
  color: #856404;
}

.history-table .inaccurate {
  background-color: #F9EDEB;
  color: #FF0000;
}



/* history page style end  */



/* Client profile style start  */
.client-profile {
  padding: 30px;
  box-shadow: 0px 4px 30px rgba(0, 0, 0, 0.06);
}

.hide-btn {
  border: 1px solid var(--light-green);
  color: var(--light-green);
  border-radius: 8px;
  padding: 5px 22px;
}

.client-profile p {
  color: var(--light-green);
}
.client-profile p span {
  font-weight: 600;
}


.client-profile .form-label {
  font-weight: 700;
}
/* Client profile style end  */

/* Responsive Adjustments */
@media (max-width: 992px) {
  .person-card {
    flex: 1 1 45%;
  }
}

@media (max-width: 768px) {
  .person-card {
    flex: 1 1 100%;
    max-width: 100%;
  }
}










/* home screen style end  */

@media screen and (min-width: 768px) and (max-width: 992px) {
  .numbers {
    flex: 1 1 calc(30.333% - 1rem);
    /* 3 items per row on tablets */
    max-width: calc(30.333% - 1rem);
  }

  .step-two {
    width: 100% !important;
    margin: auto;
    /* border: 2px solid green; */
    margin-top: 100px;
  }

  .back-btn {
    margin-top: 160px;
  }
}

@media (max-width: 480px) {
  .numbers {
    flex: 1 1 calc(30% - 1rem);
    /* 2 items per row on small screens */
    max-width: calc(37% - 1rem);
    padding: 10px;
  }

  .step-two {
    width: 100% !important;
    margin: auto;
    /* border: 2px solid red; */
  }

  .client-profile {
    padding: 10px;
  }
}

