import React, { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { toast } from 'react-toastify';

function NewCustomerForm() {
  const navigate = useNavigate();
  const location = useLocation();
  const customerType = location.state?.customerType || "new"; // "new" is the fallback
  const [showDuplicateNotice, setShowDuplicateNotice] = useState(false);
  const [isChecked, setIsChecked] = useState(true); // âœ… checked by default
  const [isSubmitting, setIsSubmitting] = useState(false);
  const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;


  console.log("Customer Type:", customerType); // Debug log

  const [formData, setFormData] = useState({
    first_name: "",
    last_name: "",
    middle_initial: "",
    dob: "",
    age: "",
    address: "",
    city: "",
    province: "",
    postal_code: "",
    home_phone: "",
    cell_phone: "",
    work_phone: "",
    email: "",
    can_email: false,
    signing_for_minor: false, // Default: No
    minors: [],
  });

  const [minorList, setMinorList] = useState([]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleMinorChange = (index, field, value) => {
    const updated = [...minorList];
    updated[index][field] = value;
    setMinorList(updated);
  };

  const addMinor = () => {
    setMinorList([...minorList, { first_name: "", last_name: "", dob: "" }]);
  };

  const removeMinor = (index) => {
    const updated = [...minorList];
    updated.splice(index, 1);
    setMinorList(updated);
  };

//  const handleSubmit = async (e) => {
//   e.preventDefault();
//   const fullData = { ...formData, minors: minorList };

//   try {
// const response = await axios.post(`${BACKEND_URL}/api/waivers`, fullData);

//     const otp = response.data.otp;
//     //toast.success("âœ… Customer created! OTP sent.");
//     //  toast.success(`Customer created and OTP sent successfully`);
//     toast.success(`Customer created and OTP sent successfully. OTP: ${otp}`);
//     navigate("/opt-verified", {
//       state: { phone: formData.cell_phone, customerType: "new" },
//     });
//    } catch (err) {
//     if (err.response && err.response.status === 409) {
//       toast.error("ðŸš« This phone number already exists. Please use a different number.");
//       setShowDuplicateNotice(true);
//     } else if (err.response && err.response.data?.error) {
//       toast.error(`âŒ ${err.response.data.error}`);
//     } else {
//       console.error(err);
//       toast.error("âŒ Error submitting form. Please try again.");
//     }
//   }

// };


// const handleSubmit = async (e) => {
//   e.preventDefault();
//   setIsSubmitting(true);
//   const fullData = { ...formData, minors: minorList };

//   try {
//     const response = await axios.post(`${BACKEND_URL}/api/waivers`, fullData);
//     const otp = response.data.otp;

//     toast.success(`Customer created and OTP sent successfully. OTP: ${otp}`);
//     navigate("/opt-verified", {
//       state: { phone: formData.cell_phone, customerType: "new" },
//     });
//   } catch (err) {
//     if (err.response && err.response.status === 409) {
//       toast.error("ðŸš« This phone number already exists. Please use a different number.");
//       setShowDuplicateNotice(true);
//     } else if (err.response && err.response.data?.error) {
//       toast.error(`âŒ ${err.response.data.error}`);
//     } else {
//       console.error(err);
//       toast.error("âŒ Error submitting form. Please try again.");
//     }
//   } finally {
//     setIsSubmitting(false);
//   }
// };

const handleSubmit = async (e) => {
  e.preventDefault();
  setIsSubmitting(true);
  const fullData = { ...formData, minors: minorList, send_otp: isChecked };

  try {
    const response = await axios.post(`${BACKEND_URL}/api/waivers`, fullData);
    const otp = response.data.otp;

    toast.success(`Customer created and OTP sent successfully. OTP: ${otp}`);
    navigate("/opt-verified", {
      state: { phone: formData.cell_phone, customerType: "new" },
    });
  } catch (err) {
    if (err.response && err.response.status === 409) {
      toast.error("ðŸš« This phone number already exists. Please use a different number.");
      setShowDuplicateNotice(true);
    } else if (err.response && err.response.data?.error) {
      toast.error(`âŒ ${err.response.data.error}`);
    } else {
      console.error(err);
      toast.error("âŒ Error submitting form. Please try again.");
    }
  } finally {
    setIsSubmitting(false);
  }
};




const handleNextClick = () => {
    navigate("/existing-customer", {
      state: { phone: formData.cell_phone },
    });
  };



useEffect(() => {
  if (formData.dob) {
    const dobDate = new Date(formData.dob);
    const today = new Date();
    let age = today.getFullYear() - dobDate.getFullYear();
    const m = today.getMonth() - dobDate.getMonth();

    if (m < 0 || (m === 0 && today.getDate() < dobDate.getDate())) {
      age--;
    }

    setFormData((prev) => ({ ...prev, age: age.toString() }));
  }
}, [formData.dob]);


  return (
    <div className="container-fluid">
      <div className="container text-center">
        <div className="row">
          <div className="col-md-2">
            <div className="back-btn">
              <Link to="/">
                <img className="img-fluid" src="/assets/img/image 298.png" alt="back-icon" /> BACK
              </Link>
            </div>
          </div>

          <div className="col-12 col-md-8 col-xl-8">
            <div className="step-two step-three">
              <div className="logo">
                <img className="img-fluid" src="/assets/img/SKATE_AND_PLAY_V08_Full_Transparency (2) 1.png" alt="logo" />
              </div>
            </div>
          </div>
        </div>

        <div className="row">
          <div className="col-md-12 col-xl-10 mx-auto">
            <h3 className="h5-heading">Your details</h3>
            <form onSubmit={handleSubmit}>
              <div className="info-table w-100">
                <table cellPadding="8" cellSpacing="0">
                  <tbody>
                    <tr>
                      <td>
                        First Name:<span className="required-star">*</span><br />
                        <input type="text" name="first_name" value={formData.first_name} onChange={handleChange} className="form-control"  required/>
                      </td>
                      <td>
                        Last Name:<span className="required-star">*</span><br />
                        <input type="text" name="last_name" value={formData.last_name} onChange={handleChange} className="form-control" required />
                      </td>
                    <td>
                      Middle Initial:<br />
                      <input
                        type="text"
                        name="middle_initial"
                        value={formData.middle_initial}
                        onChange={handleChange}
                        className="form-control"
                      />
                    </td>

                      <td>
                        DOB:<span className="required-star">*</span><br />
                        <input type="date" name="dob" value={formData.dob} onChange={handleChange} className="form-control" required />
                      </td>
                      <td>
                        Age:<span className="required-star">*</span><br />
                        <input type="number" name="age" value={formData.age} onChange={handleChange} className="form-control" required/>
                      </td>
                    </tr>

                    <tr>
                      <td colSpan="2">
                        Address:<span className="required-star">*</span><br />
                        <input type="text" name="address" value={formData.address} onChange={handleChange} className="form-control" required/>
                      </td>
                      <td>
                        City:<span className="required-star">*</span><br />
                        <input type="text" name="city" value={formData.city} onChange={handleChange} className="form-control" required/>
                      </td>
                      <td>
                        Province:<span className="required-star">*</span><br />
                        <input type="text" name="province" value={formData.province} onChange={handleChange} className="form-control" required/>
                      </td>
                      <td>
                        Postal Code:<span className="required-star">*</span><br />
                        <input type="text" name="postal_code" value={formData.postal_code} onChange={handleChange} className="form-control" required/>
                      </td>
                    </tr>

                    <tr>
                      <td>
                        Home Phone:<span className="required-star">*</span><br />
                        <input type="tel" name="home_phone" value={formData.home_phone} onChange={handleChange} className="form-control" required/>
                      </td>
                      <td>
                        Cell Phone:<span className="required-star">*</span><br />
                        <input type="tel" name="cell_phone" value={formData.cell_phone} onChange={handleChange} className="form-control" required/>
                      </td>
                     <td>
                      Work Phone:<br />
                      <input
                        type="tel"
                        name="work_phone"
                        value={formData.work_phone}
                        onChange={handleChange}
                        className="form-control"
                      />
                    </td>

                      <td>
                        Email:<span className="required-star">*</span><br />
                        <input type="email" name="email" value={formData.email} onChange={handleChange} className="form-control" />
                      </td>
                      <td>
                        Can we email?<br />
                        <label>
                          <input
                            type="radio"
                            name="can_email"
                            checked={formData.can_email === true}
                            onChange={() => setFormData((p) => ({ ...p, can_email: true }))}
                          />{" "}
                          Yes
                        </label>
                        <label className="ms-3">
                          <input
                            type="radio"
                            name="can_email"
                            checked={formData.can_email === false}
                            onChange={() => setFormData((p) => ({ ...p, can_email: false }))}
                          />{" "}
                          No
                        </label>
                      </td>
                    </tr>
                  </tbody>
                </table>
              </div>


             {/* Minor Section Toggle */}
                <div className="text-start my-4">
                  <div className="d-flex align-items-center custom-radio-wrapper">
                    <h5>I'm signing on behalf of a minor or dependent</h5>
                    <label className="ms-3">
                      <input
                        type="radio"
                        name="signing_for_minor"
                        checked={formData.signing_for_minor === true}
                        onChange={() => {
                          setFormData((p) => ({ ...p, signing_for_minor: true }));
                          // Add one minor input if list is empty
                          if (minorList.length === 0) {
                            setMinorList([{ first_name: "", last_name: "", dob: "" }]);
                          }
                        }}
                      />{" "}
                      Yes
                    </label>
                    <label className="ms-3">
                      <input
                        type="radio"
                        name="signing_for_minor"
                        checked={formData.signing_for_minor === false}
                        onChange={() => {
                          setFormData((p) => ({ ...p, signing_for_minor: false }));
                          setMinorList([]); // Clear minor list
                        }}
                      />{" "}
                      No
                    </label>
                  </div>
                </div>

                {/* Conditional Minor Form */}
                {formData.signing_for_minor && (
                  <>
                    {minorList.map((minor, index) => (
                      <div key={index} className="minor-group d-flex gap-2 mb-3">
                        <input
                          type="text"
                          className="form-control"
                          placeholder="First Name"
                          value={minor.first_name}
                          onChange={(e) => handleMinorChange(index, "first_name", e.target.value)}
                        />
                        <input
                          type="text"
                          className="form-control"
                          placeholder="Last Name"
                          value={minor.last_name}
                          onChange={(e) => handleMinorChange(index, "last_name", e.target.value)}
                        />
                        <input
                          type="date"
                          className="form-control"
                          value={minor.dob}
                          onChange={(e) => handleMinorChange(index, "dob", e.target.value)}
                        />
                        <button
                          type="button"
                          className="btn btn-danger"
                          onClick={() => removeMinor(index)}
                        >
                          Remove
                        </button>
                      </div>
                    ))}
                    <button
                      type="button"
                      onClick={addMinor}
                      className="btn btn-primary my-2"
                    >
                      Add another minor
                    </button>
                  </>
                )}


              {/* Save time checkbox */}
              {/* <div className="my-4">
                <div className="confirm-box text-start">
                  <label className="custom-checkbox-wrapper">
                    <input type="checkbox" className="custom-checkbox"  checked/>
                    <span className="custom-checkbox-label">
                      <h5>
                        Save time on your next visit! Use your phone number as a reference for future waivers. Just check the box and receive a quick validation text.
                      </h5>
                    </span>
                  </label>
                </div>
              </div> */}

              <div className="my-4">
  <div className="confirm-box text-start">
    <label className="custom-checkbox-wrapper">
      <input
        type="checkbox"
        className="custom-checkbox"
        checked={isChecked}
        onChange={() => setIsChecked((prev) => !prev)} // âœ… toggle
      />
      <span className="custom-checkbox-label">
        <h5>
          Save time on your next visit! Use your phone number as a reference for future waivers. Just check the box and receive a quick validation text.
        </h5>
      </span>
    </label>
  </div>
</div>

              <div className="buttons mb-5">
                 {showDuplicateNotice && (
                  <div className="alert alert-warning w-50 mx-auto text-center">
                    This phone number already exists. Please proceed as an existing user. <br />
                    If you want to log in,{" "}
                    <span
                      onClick={handleNextClick}
                      style={{ textDecoration: "underline", cursor: "pointer", color: "#0d6efd" }}
                    >
                      click here
                    </span>.
                  </div>
                )}
                {/* <button type="submit" className="btn btn-primary w-25">
                  Next
                </button> */}

                <button type="submit" className="btn btn-primary w-25" disabled={isSubmitting}>
  {isSubmitting ? (
    <>
      <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
      Submitting...
    </>
  ) : (
    "Next"
  )}
</button>

              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default NewCustomerForm;

src/pages/admin/components/header.js.content:
import React, { useRef } from "react";
import { useLocation, Link, useNavigate } from "react-router-dom";
import { BACKEND_URL } from '../../../config';

function Header() {
  const location = useLocation();
  const navigate = useNavigate();
  const currentPath = location.pathname;

  const [staff, setStaff] = React.useState(JSON.parse(localStorage.getItem("staff")));
  const dropdownRef = useRef(null); // Reference for dropdown

  // Listen for profile updates
  React.useEffect(() => {
    const handleProfileUpdate = () => {
      setStaff(JSON.parse(localStorage.getItem("staff")));
    };

    window.addEventListener('profileUpdated', handleProfileUpdate);
    return () => window.removeEventListener('profileUpdated', handleProfileUpdate);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("staff");
    navigate("/admin/login");
  };


  return (
    <div className="container-fluid container-header ">
     <div className="container">



  <header className="header">
    <nav className="navbar navbar-expand-lg py-2">
      <div className="container-fluid">

        {/* Logo */}
        <Link className="navbar-brand" to="/admin/home">
          <img
            src="/assets/img/SKATE_AND_PLAY_V08_Full_Transparency (2) 1.png"
            className="img-fluid"
            alt="logo"
            style={{ height: "50px" }}
          />
        </Link>

        {/* Hamburger Button */}
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNavDropdown"
          aria-controls="navbarNavDropdown"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span><i className="fa-solid fa-bars"></i></span>
        </button>


        <div className="d-flex d-lg-none align-items-center flex-wrap flex-lg-nowrap header-mobile-view ">

            {/* Notification Bell */}
            {/* <img
              src="/assets/img/bell-icon.png"
              alt="bell"
              className="me-3"
              style={{ width: "18px", height: "18px", cursor: "pointer" }}
            /> */}

            {/* Profile Dropdown */}
            <div className="dropdown" ref={dropdownRef}>
              <img
                src={
                  staff?.profile_image
                    ? `${BACKEND_URL}/${staff.profile_image}`
                    : "/assets/img/Vector.png"
                }
                alt="profile"
                className="dropdown-toggle"
                data-bs-toggle="dropdown"
                style={{
                  width: "40px",
                  height: "40px",
                  borderRadius: "50%",
                  border: "2px solid #fff",
                  objectFit: "cover",
                  cursor: "pointer"
                }}
              />

              <ul className="dropdown-menu dropdown-menu-end" style={{ padding: "10px 0", minWidth: "200px" }}>
                <li className="text-center px-3" style={{ paddingBottom: "10px", borderBottom: "1px solid #e0e0e0", marginBottom: "10px" }}>
                    <img
                      src={
                        staff?.profile_image?.startsWith("http")
                          ? staff.profile_image
                          : staff?.profile_image
                          ? `${BACKEND_URL}/${staff.profile_image}`
                          : "/assets/img/Vector.png"
                      }
                    alt="profile"
                    style={{
                      width: "50px",
                      height: "50px",
                      borderRadius: "50%",
                      marginBottom: "8px",
                      objectFit: "cover",
                    }}
                  />
                  <h6 style={{ margin: 0, fontSize: "14px", fontWeight: "600" }}>{staff?.name || "Admin User"}</h6>
                </li>
                <li style={{ margin: "5px 0" }}><Link className="dropdown-item" to="/admin/change-password" style={{ padding: "8px 16px" }}>Change Password</Link></li>
                <li style={{ margin: "5px 0" }}><Link className="dropdown-item" to="/admin/update-profile" style={{ padding: "8px 16px" }}>Update Profile</Link></li>
                <li style={{ margin: "5px 0" }}><span className="dropdown-item" style={{ cursor: "pointer", padding: "8px 16px" }} onClick={handleLogout}>Sign Out</span></li>
              </ul>
            </div>
          </div>



        {/* Nav Items */}
        <div className="collapse navbar-collapse" id="navbarNavDropdown">
          <ul className="navbar-nav me-auto mb-2 mb-lg-0 gap-4">
            <NavItem
              to="/admin/home"
              icon="/assets/img/Icon.png"
              label="Home"
              isActive={currentPath === "/admin/home"}
            />
            <NavItem
              to="/admin/staff-list"
              icon="/assets/img/team.png"
              label="Team"
              isActive={currentPath === "/admin/staff-list"}
            />
            <NavItem
              to="/admin/history"
              icon="/assets/img/refresh.png"
              label="History"
              isActive={currentPath === "/admin/history"}
            />
            <NavItem
              to="/admin/feedback-list"
              icon="/assets/img/review.png"
              label="Feedback"
              isActive={currentPath === "/admin/feedback-list"}
            />
          </ul>

          {/* Right Side Icons */} 
          {/* <div className="d-flex align-items-center flex-wrap flex-lg-nowrap"> */}
            <div className="d-none d-lg-flex align-items-center flex-wrap flex-lg-nowrap">

            {/* Notification Bell */}
            {/* <img
              src="/assets/img/bell-icon.png"
              alt="bell"
              className="me-3"
              style={{ width: "24px", height: "24px", cursor: "pointer" }}
            /> */}

            {/* Profile Dropdown */}
            <div className="dropdown" ref={dropdownRef}>
              <img
                src={
                  staff?.profile_image
                    ? `${BACKEND_URL}/${staff.profile_image}`
                    : "/assets/img/Vector.png"
                }
                alt="profile"
                className="dropdown-toggle"
                data-bs-toggle="dropdown"
                style={{
                  width: "40px",
                  height: "40px",
                  borderRadius: "50%",
                  border: "2px solid #fff",
                  objectFit: "cover",
                  cursor: "pointer"
                }}
              />

              <ul className="dropdown-menu dropdown-menu-end" style={{ padding: "10px 0", minWidth: "200px" }}>
                <li className="text-center px-3" style={{ paddingBottom: "10px", borderBottom: "1px solid #e0e0e0", marginBottom: "10px" }}>
                  <img
                    src={
                      staff?.profile_image?.startsWith("http")
                        ? staff.profile_image
                        : staff?.profile_image
                        ? `${BACKEND_URL}/${staff.profile_image}`
                        : "/assets/img/Vector.png"
                    }
                    alt="profile"
                    style={{
                      width: "50px",
                      height: "50px",
                      borderRadius: "50%",
                      marginBottom: "8px",
                      objectFit: "cover",
                    }}
                  />
                  <h6 style={{ margin: 0, fontSize: "14px", fontWeight: "600" }}>{staff?.name || "Admin User"}</h6>
                </li>
                <li style={{ margin: "5px 0" }}><Link className="dropdown-item" to="/admin/change-password" style={{ padding: "8px 16px" }}>Change Password</Link></li>
                <li style={{ margin: "5px 0" }}><Link className="dropdown-item" to="/admin/update-profile" style={{ padding: "8px 16px" }}>Update Profile</Link></li>
                <li style={{ margin: "5px 0" }}><span className="dropdown-item" style={{ cursor: "pointer", padding: "8px 16px" }} onClick={handleLogout}>Sign Out</span></li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </nav>
  </header>
</div>

    </div>
  );
}

function NavItem({ to, icon, label, isActive }) {
  return (
    <div>
      <Link to={to} className={`nav-link${isActive ? " active" : ""}`}>
        <div className="d-flex gap-1 flex-column text-white align-items-center">
          <div>
            <img src={icon} alt={label} />
          </div>
          <div>{label}</div>
        </div>
      </Link>
    </div>
  );
}

export default Header;

