  }
}

@media (max-width: 480px) {
  .numbers {
    flex: 1 1 calc(30% - 1rem);
    /* 2 items per row on small screens */
    max-width: calc(37% - 1rem);
    padding: 10px;
  }

  .step-two {
    width: 100% !important;
    margin: auto;
    /* border: 2px solid red; */
  }

  .client-profile {
    padding: 10px;
  }
}

