                        if (customerType === "new") {
                          navigate("/verify-otp", { state: { phone, customerType } });
                        } else {
                          navigate("/confirm-info", { state: { phone, customerType } });
                        }
                      }}>

                        <img src="/assets/img/image 298.png" className="img-fluid" alt="back" /> BACK

                      </div>
          </div>



          <div className="col-12 col-md-8 col-xl-8">
