          </div>

          <div className="col-12 col-md-8 col-xl-8">
            <div className="step-two step-three">
              <div className="logo">
                <img
                  className="img-fluid"
                  src="/assets/img/SKATE_AND_PLAY_V08_Full_Transparency (2) 1.png"
                  alt="logo"
                />
              </div>
              <div>
                <h5 className="h5-headingcccc">Rule Reminder</h5>
              </div>
            </div>
          </div>
        </div>

        {/* Main Content Section */}
