  .back-btn img {
    width: 22%;
  }


  .signature-section {
    margin: 5px auto 5px auto;
    width: 100% !important;
  }

  .signature-section canvas {
    width: 100% !important;
  }

  /* table responsive start */

  .info-table {
    width: 100%;
    overflow-x: auto;
  }

