    margin-bottom: 1rem;
    border-bottom: 1px solid #ddd;
    padding-bottom: 1rem;
  }

  .info-table td {
    border: none !important;
    padding: 0.5rem 0;
  }

  .info-table table tr td input {
    border: 1px solid #e4dfdf;
  }

  .info-table td input,
  .info-table td label {
    width: 100% !important;
  }

  /* .info-table td input,
  .info-table td.custom-email-label label {
    width: 25% !important;
