@media (max-width: 480px) {

  .custom-search-mobile-view {
    width: 80% !important;
  }

  div.dt-container div.dt-layout-row:not(.dt-layout-table) {
        display: flex !important;
    }

  .dt-search label {
    display: none;
  }

  .dt-length label {
    display: none;
  }

  .all-waiver {
    padding: 12px 5px;
    font-size: 15px;
    width: 80%;
  }

  .h3-heading  {
    font-size: 20px !important;
  }


  .numbers {
    flex: 1 1 calc(30% - 1rem);
    /* 2 items per row on small screens */
    max-width: calc(37% - 1rem);
    padding: 10px;
    font-size: 18px;
  }

  .step-two {
    width: 100% !important;
    margin: auto;
    /* border: 2px solid red; */
  }

  .client-profile {
    padding: 10px;
  }


  .logo-img {
    width: 90%;
    padding-bottom: 10px;
  }

  .existing-customer {
    margin: 0 20px;
    padding: 12px 20px;
    width: 270px;
  }

  .new-waiver {
    margin: 0 20px;
    padding: 12px 20px;
    width: 270px;
  }

  .back-btn {
    text-align: start;
    margin-top: 20px;
    font-weight: 600;
    display: flex;
    align-items: center;
    gap: 5px;
