  .back-btn a {
    font-size: 14px;
  }

  /* .back-btn a img {
    width: 7%;
  } */
  .back-btn img {
    width: 8%;
  }


  .step-two p {
    font-size: 12px;
    margin: 6px 0 30px 0;
  }

  .step-two H5 {
    font-size: 18px;
  }

  .custom-radio-wrapper h5 {
    font-size: 18px;
  }

  .custom-radio-wrapper {
    flex-wrap: wrap;
    gap: 8px;
  }

  .custom-radio-wrapper label {
    margin: 0 !important;
    padding: 0px 15px 0 0;
  }

  .minor-group {
    display: flex;
    gap: 20px !important;
    flex-wrap: wrap;
    margin-bottom: 20px !important;
  }

  .custom-checkbox-label h5 {
    font-size: 18px;
  }

  .custom-checkbox-wrapper {
    margin: 0;
  }

  .minor-section .buttons {
    gap: 10px;
  }

  .minor-section .btn {
    padding: 10px 15px;
    font-size: 13px;
    width: auto !important;
    margin-bottom: 5px;
  }


  .signature-section {
    margin: 5px auto 5px auto;
    width: 100% !important;
  }

  .signature-section canvas {
    width: 100% !important;
  }

  .rule-images div {
    flex: 0 0 100%;
    /* Two items per row */
  }

  .rule-images h5 {
    font-size: 18px;
  }

  .confirm-btn {
    width: 30%;
  }


  .custom-search-box {
    width: 200px;
    padding: 8px 12px;
  }

  .search-minors {
    width: 70%;
  }

  .return-btn {
    padding: 10px;
    width: 100%;
    text-align: center;
  }

  .info-table tr td br {
    display: none;
  }

  .info-table tr td span {
    padding-left: 10px;
  }

}

@media (max-width: 350px) {

div.dt-container div.dt-layout-row:not(.dt-layout-table) {
        display: flex !important;
    }

  .dt-search label {
    display: none;

  }

 .dt-layout-cell.dt-layout-end {
    width: 73%;
}

  .dt-length label {
    display: none;
  }

  .custom-search-box {
    width: 60%;
    padding: 7px 10px;
  }


  .back-btn a {
