import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export default function FeedbackPage() {
  const { id } = useParams();
  const [feedback, setFeedback] = useState('');
  const [submitted, setSubmitted] = useState(false);
  const [customerName, setCustomerName] = useState('');
  const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;

  useEffect(() => {
    // Fetch customer name
    axios
      .get(`${BACKEND_URL}/api/waivers/rate/${id}`)
      .then((res) => {
        setCustomerName(res.data.first_name);
      })
      .catch((err) => console.error('Failed to fetch customer name:', err));
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {

        await axios.post(`${BACKEND_URL}/api/waivers/feedback`, {
      id,
      message: feedback,
    });  


      await axios.post(`${BACKEND_URL}/api/waivers/send-feedback`, {
        id,
        message: feedback,
      });
      toast.success('Feedback submitted successfully!');
      setSubmitted(true);
    } catch (err) {
      toast.error('Failed to send feedback. Try again later.');
    }
  };

  return (
    <>
      <ToastContainer position="top-right" autoClose={2000} />
      <div className="container-fluid min-vh-100 d-flex align-items-center justify-content-center bg-light">
        <div className="container text-center">
          <div className="row justify-content-center">
            <div className="col-12 col-md-10 col-lg-8">
              <div className="p-4 bg-white rounded shadow-sm">
                <img
                  className="img-fluid mb-3"
                  src="/assets/img/SKATE_AND_PLAY_V08_Full_Transparency (2) 1.png"
                  alt="logo"
                  style={{ maxWidth: '200px' }}
                />
                <h5 className="mb-3">Hi {customerName},</h5>
                {!submitted ? (
                  <>
                    <h4>Weâ€™re sorry your experience wasnâ€™t perfect</h4>
                    <p>Please let us know what we could do better:</p>
                    <form onSubmit={handleSubmit}>
                      <textarea
                        className="form-control mb-3"
                        value={feedback}
                        onChange={(e) => setFeedback(e.target.value)}
                        rows="5"
                        required
                      />
                      <button className="btn btn-primary" type="submit">
                        Submit Feedback
                      </button>
                    </form>
                  </>
                ) : (
                  <>
                    <h4 className="text-success mb-3">Thank You!</h4>
                    <p>Your feedback helps us improve your experience.</p>
                  </>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

src/pages/admin/homeOLD.js.content:
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Header from './components/header';
import { toast } from 'react-toastify';

function HomePage() {
  const [customers, setCustomers] = useState([]);
  const [selected, setSelected] = useState(null); // { customer, waiverId }
  const staff = JSON.parse(localStorage.getItem("staff")) || {};
  const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;

  const fetchData = async () => {
    try {
      const res = await axios.get(`${BACKEND_URL}/api/waivers/getAllCustomers`);
      setCustomers(res.data);
    } catch (err) {
      console.error('Error fetching customers:', err);
      toast.error('Failed to load waivers');
    }
  };

  const verifyWaiver = async (waiverId, verified_by_staff) => {
    try {
      const res = await axios.post(`${BACKEND_URL}/api/waivers/verify/${waiverId}`, {
        staff_id: staff.id,
        verified_by_staff
      });
      toast.success(res.data.message);
      fetchData();
    } catch (err) {
      console.error('Error verifying waiver:', err);
      toast.error("Verification failed");
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <>
      <Header />
      <div className="container">
        <div className="row my-4">
          <div className="col-md-4 mx-auto text-center">
            <h5 className="h5-heading">{customers.length} Waiver{customers.length !== 1 ? 's' : ''} Completed</h5>
            <input className="form-control" placeholder="Search Name or Phone Number" />
          </div>
        </div>

        <div className="row">
          <div className="col-md-12">
            <div className="card-grid mb-5">
              {customers.map((c) => (
                <PersonCard
                  key={c.id}
                  customer={c}
                  onDetails={(waiverId) => setSelected({ customer: c, waiverId })}
                />
              ))}
            </div>
          </div>
        </div>

        {selected && (
          <DetailsModal
            customer={selected.customer}
            waiverId={selected.waiverId}
            onClose={() => setSelected(null)}
            onVerify={(waiverId, verified_by_staff) => {
              verifyWaiver(waiverId, verified_by_staff);
              setSelected(null);
            }}
          />
        )}
      </div>
    </>
  );
}

function PersonCard({ customer, onDetails }) {
  return (
    <div className="person-card">
      <div className="card-header d-flex justify-content-between">
        <div className="card-name">
          <h5>{customer.first_name} {customer.last_name}</h5>
          {customer.minors.map((m, i) => (
            <p key={i}>{m.first_name} {m.last_name}</p>
          ))}
        </div>
        <img src="/assets/img/Closed.png" alt="icon" />
      </div>
      <div className="card-footer">
        <span>Verify their ID - No enter if NO ID</span>
        <button className="details-btn btn btn-primary" onClick={() => onDetails(customer.waiver_id)}>
          Details
        </button>
      </div>
    </div>
  );
}

function DetailsModal({ customer, waiverId, onClose, onVerify }) {
  const [showConfirm, setShowConfirm] = useState(false);
  const [actionType, setActionType] = useState(null); // 'verify' or 'notAccurate'

  useEffect(() => {
    const handleEsc = (e) => {
      if (e.key === "Escape") onClose();
    };
    document.addEventListener("keydown", handleEsc);
    return () => document.removeEventListener("keydown", handleEsc);
  }, [onClose]);

  const handleBackdropClick = (e) => {
    if (
      e.target.classList.contains("modal-backdrop") ||
      e.target.classList.contains("modal")
    ) {
      onClose();
    }
  };

  const handleConfirmAction = () => {
    if (actionType === "verify") {
      onVerify(waiverId, 1); // Verified
    } else if (actionType === "notAccurate") {
      onVerify(waiverId, 2); // Not accurate
    }
    setShowConfirm(false);
    onClose();
  };

  const handleCancel = () => {
    setShowConfirm(false);
  };

  return (
    <>
      {/* Main Modal Backdrop */}
      <div className="modal-backdrop fade show" onClick={handleBackdropClick}></div>

      {/* Main Modal */}
      <div
        className="modal show fade d-block"
        tabIndex="-1"
        role="dialog"
        onClick={handleBackdropClick}
      >
        <div className="modal-dialog modal-dialog-centered" role="document">
          <div className="modal-content p-3 position-relative">
            <div className="modal-body text-center">
              <h5>
                {customer.first_name} {customer.last_name} |{" "}
                {customer.dob ? new Date(customer.dob).toISOString().slice(0, 10) : ""}
              </h5>

              <p className="mt-2">
                <strong>Address:</strong>{" "}
                {customer.address
                  ? `${customer.address}, ${customer.city || ""}, ${customer.province || ""} ${customer.postal_code || ""}`
                  : "Not Provided"}
              </p>

              {customer.minors && customer.minors.length > 0 && (
                <>
                  <h6 className="mt-3">Minors:</h6>
                  {customer.minors.map((m, i) => (
                    <p key={i}>
                      {m.first_name} {m.last_name} |{" "}
                      {m.dob ? new Date(m.dob).toISOString().slice(0, 10) : ""}
                    </p>
                  ))}
                </>
              )}
            </div>

            <div className="modal-footer justify-content-center gap-3">
              <button
                onClick={() => {
                  setActionType("verify");
                  setShowConfirm(true);
                }}
                className="btn details-btn"
              >
                I VERIFIED THEIR ID
              </button>
              <button
                onClick={() => {
                  setActionType("notAccurate");
                  setShowConfirm(true);
                }}
                className="btn btn-danger"
              >
                âš  Information is NOT accurate
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Confirmation Modal */}
      {showConfirm && (
        <>
          <div className="modal-backdrop fade show"></div>
          <div className="modal show fade d-block" tabIndex="-1" role="dialog">
            <div className="modal-dialog modal-dialog-centered" role="document">
              <div className="modal-content p-3 text-center">
                <div className="modal-body">
                  <p>
                    {actionType === "verify"
                      ? "Are you sure you want to verify this ID?"
                      : "Are you sure this information is NOT accurate?"}
                  </p>
                  <div className="d-flex justify-content-center gap-3 mt-3">
                    <button className="btn btn-success" onClick={handleConfirmAction}>
                      Yes
                    </button>
                    <button className="btn btn-secondary" onClick={handleCancel}>
                      Cancel
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </>
      )}
    </>
  );
}

export default HomePage;

src/pages/StarRatingPage.js.content:
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Rating } from 'react-simple-star-rating';
import axios from 'axios';
import { BACKEND_URL, GOOGLE_REVIEW_LINK } from '../config';

function StarRatingPage() {
  const { id } = useParams(); // waiver id
  const navigate = useNavigate();
  const [customerName, setCustomerName] = useState('');
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);


useEffect(() => {
  setLoading(true);
  axios.get(`${BACKEND_URL}/api/waivers/rate/${id}`)
    .then(res => {
      const { first_name, last_name } = res.data;
      setCustomerName(`${first_name} ${last_name}`);
    })
    .catch(err => console.error('Failed to fetch customer:', err))
    .finally(() => setLoading(false));
}, [id]);


  const handleRating = async (rate) => {
    setSubmitting(true);
    try {
  const response = await axios.post(`${BACKEND_URL}/api/waivers/rate/${id}`, { rating: rate });
  const feedbackId = response.data.feedbackId;

  setTimeout(() => {
    if (rate === 5) {
         window.location.href = GOOGLE_REVIEW_LINK; 
    } else {
      navigate(`/feedback?userId=${id}&feedbackId=${feedbackId}`);

    }
  }, 1000);
} catch (error) {
  console.error('Rating error:', error);
  setSubmitting(false);
}

  };

  return (
     <div className="container-fluid min-vh-100 d-flex align-items-center justify-content-center bg-light">
      <div className="container text-center">
        <div className="row justify-content-center">
          <div className="col-12 col-md-10 col-lg-8">
            <div className="p-4 bg-white rounded shadow-sm">
              <img
                className="img-fluid mb-3"
                src="/assets/img/SKATE_AND_PLAY_V08_Full_Transparency (2) 1.png"
                alt="logo"
                style={{ maxWidth: '200px' }}
              />
              {loading ? (
                <p>Loading...</p>
              ) : (
                <>
                  <h5 className="mb-3">Hi {customerName},</h5>
                  <h5 className="fw-bold mb-2">We'd love to know how your experience was.</h5>
                  <p className="fw-bold mb-3">Please take a few seconds to rate your visit:</p>
                  {submitting && <p className="text-primary">Submitting rating...</p>}

                  <div className="d-flex justify-content-center mb-4">
                    <Rating
                        onClick={handleRating}
                        size={50}
                        initialValue={0}
                        allowFraction={false}
                        transition
                        readonly={submitting}
                      />
                  </div>



                  <h6 className="mb-2">It only takes a moment and really helps us improve.</h6>
                  <h6>Thanks for being part of the fun â€” we hope to see you again soon!</h6>
                </>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default StarRatingPage;

src/pages/admin/HistoryOld.js.content:
import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import axios from 'axios';
import Skeleton from 'react-loading-skeleton';
import 'react-loading-skeleton/dist/skeleton.css';
import Header from './components/header';
import { convertToEST } from "../../utils/time";

function HistoryPage() {
  const [waivers, setWaivers] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [filter, setFilter] = useState('All');
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(true);

  const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;

  useEffect(() => {
    setLoading(true);
    axios.get(`${BACKEND_URL}/api/waivers/getallwaivers`)
      .then(res => {
        setWaivers(res.data);
        setFiltered(res.data);
      })
      .catch(err => {
        console.error("Error fetching waivers:", err);
      })
      .finally(() => setLoading(false));
  }, [BACKEND_URL]);

  // Filter based on tab selection (All, Confirmed, Unconfirmed)
  useEffect(() => {
    let data = waivers;
    if (filter !== 'All') {
      data = waivers.filter(w => filter === 'Confirmed' ? w.status === 1 : w.status === 0);
    }

    // Apply search filter as well
    if (search.trim() !== "") {
      data = data.filter((w) => {
        const fullName = `${w.first_name} ${w.last_name}`.toLowerCase();
        const minorNames = (w.minors || []).map(m => `${m.first_name} ${m.last_name}`.toLowerCase());
        return (
          fullName.includes(search.toLowerCase()) ||
          minorNames.some(name => name.includes(search.toLowerCase()))
        );
      });
    }

    setFiltered(data);
  }, [filter, search, waivers]);

  return (
    <>
      <Header />

      <div className="container mt-5">
        <div className="row">
          <div className="col-md-10 mx-auto">
            {/* Search Bar */}
            <div className="custom-search-box mb-4">
              <span className="search-icon">
                <img src="/assets/img/solar_magnifer-outline.png" alt="Search" />
              </span>
              <input
                type="text"
                className="form-control"
                placeholder="Search by name or minors"
                value={search}
                onChange={(e) => setSearch(e.target.value)}
              />
            </div>

            {/* Tabs */}
            <div className="d-flex gap-5 align-items-center my-4">
              <div
                className={`all-waiver ${filter === 'All' ? 'active-tab' : ''}`}
                onClick={() => setFilter('All')}
              >
                All Waiver
              </div>
              <div
                className={`all-waiver ${filter === 'Confirmed' ? 'active-tab' : ''}`}
                onClick={() => setFilter('Confirmed')}
              >
                Confirmed
              </div>
              <div
                className={`all-waiver ${filter === 'Unconfirmed' ? 'active-tab' : ''}`}
                onClick={() => setFilter('Unconfirmed')}
              >
                Unconfirmed
              </div>
            </div>

            {/* Table */}
            <div className="history-table">
              <table className="table">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Minors</th>
                    <th>Signed Date & Time</th>
                    <th>Status</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {loading ? (
                    // Skeleton Rows
                    Array.from({ length: 5 }).map((_, i) => (
                      <tr key={i}>
                        <td><Skeleton width={50} /></td>
                        <td><Skeleton width={120} /></td>
                        <td><Skeleton width={100} /></td>
                        <td><Skeleton width={140} /></td>
                        <td><Skeleton width={100} /></td>
                        <td><Skeleton width={50} /></td>
                      </tr>
                    ))
                  ) : filtered.length === 0 ? (
                    <tr>
                      <td colSpan="6" className="text-center">
                        No records found
                      </td>
                    </tr>
                  ) : (
                    filtered.map((entry, index) => (
                      <tr key={index}>
                        <td>{entry.id}</td>
                        <td>{entry.first_name} {entry.last_name}</td>
                        <td>
                          {entry.minors?.map((minor, i) => (
                            <div key={i}>{minor.first_name} {minor.last_name}</div>
                          ))}
                        </td>
                        <td>
                          {entry.signed_at
                            ? convertToEST(entry.signed_at, "YYYY-MM-DD hh:mm A")
                            : "N/A"}
                        </td>
                        <td>
                          <span className={`status-badge ${entry.status === 1 ? 'confirmed' : 'unconfirmed'}`}>
                            {entry.status === 1 ? 'Confirmed' : 'Unconfirmed'}
                          </span>
                        </td>
                        <td className="text-center">
                          <Link to={`/admin/client-profile/${entry.id}`}>
                            <img src="/assets/img/Group 2474.png" alt="Action" />
                          </Link>
                        </td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default HistoryPage;

