    {/* Review SMS */}
    <div style={{ marginTop: "10px" }}>
      <strong>Review SMS:</strong>{" "}
      <span
        className={`status-badge ${
          data.rating_sms_sent === 1 ? "confirmed" : "unconfirmed"
        }`}
      >
        {data.rating_sms_sent === 1 ? "Mail SMS" : "Not Sent"}
      </span>
    </div>

    {/* Action */}
    <div style={{ marginTop: "10px" }}>
      <strong>Action:</strong>{" "}
      <div className="d-flex gap-3">
        <i
          className="fas fa-eye"
          style={{ cursor: "pointer" }}
          onClick={() => navigate(`/admin/client-profile/${data.id}`)}
        />
        <i
          className="fas fa-trash"
          style={{ cursor: "pointer", color: "red" }}
          onClick={() => openModal(data, "delete")}
        />
      </div>
    </div>
  </div>
);

  return (
    <>
      <Header />
      <div className="container mt-5">
        <div className="row">
          <div className="col-md-12">

            {/* Search & filter */}
            <div className="d-flex flex-wrap justify-content-between mb-4">
              <div className="custom-search-box mb-2 custom-search-mobile-view">
                <span className="search-icon">
                  <img src="/assets/img/solar_magnifer-outline.png" alt="Search" />
                </span>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Search by name or minors"
                  value={search}
                  onChange={(e) => setSearch(e.target.value)}
                />
              </div>

              <div className="d-flex gap-2  gap-md-2 align-items-center mb-2">
                {['All', 'Confirmed', 'Unconfirmed', 'Inaccurate'].map(type => (
                  <div
                    key={type}
                    className={`all-waiver ${filter === type ? 'active-tab' : ''}`}
                    onClick={() => setFilter(type)}
                    style={{ cursor: "pointer" }}
                  >
                    {type}
                  </div>
                ))}
              </div>
            </div>

            {loading ? (
              <Skeleton height={50} count={5} />
            ) : (
              <div class="history-table">
              <DataTable
                columns={isMobile ? mobileColumns : desktopColumns}
                data={filtered}
                pagination
                responsive
                highlightOnHover
                noHeader
                keyField="waiver_id"
                expandableRows={isMobile}
                expandableRowsComponent={ExpandedComponent}
              />
              </div>
            )}

          </div>
        </div>
      </div>

      {/* Modal */}
      {showModal && selectedEntry && (
        <div className="modal show d-block" style={{ backgroundColor: "rgba(0,0,0,0.5)" }} tabIndex="-1">
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">
                  {modalType === "delete" ? "Delete Waiver" : "Change Status"}
                </h5>
                <button type="button" className="btn-close" onClick={closeModal}></button>
              </div>
              <div className="modal-body">
                {modalType === "delete" ? (
                  <p>Are you sure you want to delete this waiver for <strong>{selectedEntry.first_name} {selectedEntry.last_name}</strong>?</p>
                ) : (
                  <p>
                    Are you sure you want to <strong>{selectedEntry.status === 1 ? "Unconfirm" : "Confirm"}</strong> this waiver for <strong>{selectedEntry.first_name} {selectedEntry.last_name}</strong>?
                  </p>
                )}
              </div>
               <div className="modal-footer">
                <button className="btn btn-secondary" onClick={closeModal}>Cancel</button>
                <button className="btn btn-danger" onClick={handleConfirmModalAction}>
                  Yes, {modalType === "delete" ? "Delete" : selectedEntry.status === 1 ? "Unconfirm" : "Confirm"}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

export default HistoryPage;

src/pages/admin/login.js.content:
import React, { useState } from 'react';
import { useNavigate, Link } from "react-router-dom";
import axios from 'axios';
import { toast } from 'react-toastify';
import { BACKEND_URL } from '../../config';

function LoginPage() {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [remember, setRemember] = useState(false);
  const [loading, setLoading] = useState(false);

  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!email.trim()) {
      toast.error("Email is required");
      return;
    }

    if (!validateEmail(email)) {
      toast.error("Please enter a valid email address");
      return;
    }

    if (!password.trim()) {
      toast.error("Password is required");
      return;
    }

    setLoading(true);

    try {
      const response = await axios.post(`${BACKEND_URL}/api/staff/login`, {
        email,
        password,
      });

      toast.success(response.data.message);
      console.log("Logged in staff:", response.data.staff);

      // Save token and staff info
      localStorage.setItem("token", response.data.token);
      localStorage.setItem("staff", JSON.stringify(response.data.staff));

      navigate("/admin/home");
    } catch (err) {
      if (err.response) {
        toast.error(err.response.data.error || err.response.data.message || "Invalid email or password");
      } else {
        toast.error("Unable to connect. Please check your internet connection and try again.");
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container-fluid">
      <div className="container">
        <div className="row">
          <div className="col-12 col-md-6 col-xl-4 mx-auto">
            <div className="login-container">
              <div className="logo text-center mb-4">
                <img
                  className="img-fluid"
                  src="/assets/img/SKATE_AND_PLAY_V08_Full_Transparency (2) 1.png"
                  alt="logo"
                />
              </div>

              <form className="login-form" onSubmit={handleLogin}>
                <div className="text-center mb-4">
                  <h2 className="h5-heading">Welcome Back</h2>
                  <p className="subtitle">Please login to your account</p>
                </div>

                <div className="mb-3">
                  <label htmlFor="email" className="form-label">Email</label>
                  <input
                    type="email"
                    className="form-control"
                    placeholder="you@example.com"
                    id="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="password" className="form-label">Password</label>
                  <input
                    type="password"
                    className="form-control"
                    placeholder="Password"
                    id="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                  />
                </div>

                <div className="actions mb-3">
                  <label>
                    <input
                      type="checkbox"
                      checked={remember}
                      onChange={() => setRemember(!remember)}
                    />{" "}
                    Remember me
                  </label>
                </div>

                <button type="submit" className="login-btn btn btn-primary w-100" disabled={loading}>
                  {loading ? "Logging in..." : "Login"}
                </button>

             <p className="signup-text text-center mt-3">
  Donâ€™t have an account? <Link to="/admin/forgot-password">Forgot password?</Link>
</p>

              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default LoginPage;

