import React, { useEffect, useState } from "react";
import axios from "../../utils/axios";
import Header from "./components/header";
import { convertToEST } from "../../utils/time";
import DataTable from 'react-data-table-component';
import Skeleton from 'react-loading-skeleton';
import 'react-loading-skeleton/dist/skeleton.css';
import { BACKEND_URL } from '../../config';

const AdminFeedbackPage = () => {
  const [feedbackList, setFeedbackList] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(true);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    (async () => {
      try {
        const { data } = await axios.get(
          `${BACKEND_URL}/api/feedback/list`
        );
        const sorted = data.sort(
          (a, b) => new Date(b.created_at) - new Date(a.created_at)
        );
        setFeedbackList(sorted);
        setFiltered(sorted);
      } catch (err) {
        console.error("Failed to fetch feedback", err);
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  useEffect(() => {
    if (search.trim() === "") {
      setFiltered(feedbackList);
    } else {
      const lowerSearch = search.toLowerCase();
      const filtered = feedbackList.filter(f => {
        const fullName = `${f.first_name || ""} ${f.last_name || ""}`.toLowerCase();
        const staffName = (f.staff_name || "").toLowerCase();
        const message = (f.message || "").toLowerCase();
        const issue = (f.issue || "").toLowerCase();
        return fullName.includes(lowerSearch) || 
               staffName.includes(lowerSearch) || 
               message.includes(lowerSearch) ||
               issue.includes(lowerSearch);
      });
      setFiltered(filtered);
    }
  }, [search, feedbackList]);

  const desktopColumns = [
    { name: "#", cell: (row, index) => index + 1, width: "60px", sortable: true },
    {
      name: "Customer",
      selector: row => `${row.first_name || ""} ${row.last_name || ""}`,
      sortable: true,
      cell: row => (
        <span title={`${row.first_name || ""} ${row.last_name || ""}`}>
          {row.first_name || ""} {row.last_name || ""}
        </span>
      )
    },
    {
      name: "Rating",
      selector: row => row.rating || "-",
      sortable: true,
      width: "100px"
    },
    {
      name: "Issue",
      selector: row => row.issue || "-",
      sortable: true,
      cell: row => <span title={row.issue || "-"}>{row.issue || "-"}</span>
    },
    {
      name: "Staff Name",
      selector: row => row.staff_name || "-",
      sortable: true,
      cell: row => <span title={row.staff_name || "-"}>{row.staff_name || "-"}</span>
    },
    {
      name: "Message",
      selector: row => row.message || "-",
      sortable: true,
      cell: row => <span title={row.message || "-"}>{row.message || "-"}</span>,
      wrap: true,
      grow: 2
    },
    {
      name: "Date",
      selector: row => row.created_at ? convertToEST(row.created_at) : "-",
      sortable: true,
      wrap: true,
      minWidth: "150px"
    }
  ];

  const mobileColumns = [
    {
      name: "Customer",
      selector: row => `${row.first_name || ""} ${row.last_name || ""}`,
      sortable: true
    },
    {
      name: "Rating",
      selector: row => row.rating || "-",
      sortable: true
    }
  ];

  const ExpandedComponent = ({ data }) => (
    <div style={{ padding: "10px 20px" }}>
      <div>
        <strong>Issue:</strong> {data.issue || "-"}
      </div>
      <div style={{ marginTop: "10px" }}>
        <strong>Staff Name:</strong> {data.staff_name || "-"}
      </div>
      <div style={{ marginTop: "10px" }}>
        <strong>Message:</strong> {data.message || "-"}
      </div>
      <div style={{ marginTop: "10px" }}>
        <strong>Date:</strong> {data.created_at ? convertToEST(data.created_at) : "-"}
      </div>
    </div>
  );

  return (
    <>
      <Header />
      <div className="container mt-5">
        <div className="row">
          <div className="col-md-12">

            <h5 className="h5-heading my-3 pb-3">Customer Feedback</h5>

            <div className="d-flex flex-wrap justify-content-between mb-4">
              <div className="custom-search-box mb-2 custom-search-mobile-view">
                <span className="search-icon">
                  <img src="/assets/img/solar_magnifer-outline.png" alt="Search" />
                </span>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Search feedback..."
                  value={search}
                  onChange={(e) => setSearch(e.target.value)}
                />
              </div>
            </div>

            {loading ? (
              <Skeleton height={50} count={5} />
            ) : (
              <div className="history-table">
                <DataTable
                  columns={isMobile ? mobileColumns : desktopColumns}
                  data={filtered}
                  pagination
                  responsive
                  highlightOnHover
                  noHeader
                  keyField="id"
                  expandableRows={isMobile}
                  expandableRowsComponent={ExpandedComponent}
                />
              </div>
            )}

          </div>
        </div>
      </div>
    </>
  );
};

export default AdminFeedbackPage;

src/pages/admin/AddStaff.js.content:
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "../../utils/axios";
import { toast } from "react-toastify";
import Header from "./components/header";
import { BACKEND_URL } from '../../config';

function AddStaff() {
    const navigate = useNavigate();
  const [form, setForm] = useState({ name: "", email: "", role: "" });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.name.trim()) {
      return toast.error("Name is required");
    }

    if (!form.email.trim()) {
      return toast.error("Email is required");
    }

    if (!validateEmail(form.email)) {
      return toast.error("Please enter a valid email address");
    }

    if (!form.role) {
      return toast.error("Please select a role");
    }

    setLoading(true);

    try {
      const res = await axios.post(`${BACKEND_URL}/api/staff/addstaff`, form);
      toast.success(res.data.message || "Staff added successfully! A setup email has been sent.");
      navigate("/admin/staff-list");
      setForm({ name: "", email: "", role: "" });
    } catch (error) {
      toast.error(error.response?.data?.error || "Unable to add staff member. Please check the details and try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <Header />
      <div className="container">
        <div className="row">
          <div className="col-12 col-xl-7 col-md-10 mx-auto my-5">
            <div className="client-profile">
              <div>
                <h5 className="h5-heading">Add New Staff</h5>
                <div>
                  <img
                    className="img-fluid my-3"
                    src="/assets/img/Vector.png"
                    alt="profile-img"
                  />
                </div>
              </div>

              <form onSubmit={handleSubmit} className="add-staff-input">
                {/* Name Field */}
                <div className="mb-3">
                  <label className="form-label">Name</label>
                  <input
                    type="text"
                    name="name"
                    value={form.name}
                    onChange={handleChange}
                    className="form-control"
                    placeholder="Enter staff name"
                    disabled={loading}
                  />
                </div>

                {/* Email Field */}
                <div className="mb-3">
                  <label className="form-label">Email</label>
                  <input
                    type="email"
                    name="email"
                    value={form.email}
                    onChange={handleChange}
                    className="form-control"
                    placeholder="Enter staff email"
                    disabled={loading}
                  />
                  <small className="text-muted">An account setup email will be sent to this address</small>
                </div>

                {/* Role Select Box */}
                <div className="mb-4">
                  <label className="form-label">Role</label>
                  <select
                    name="role"
                    value={form.role}
                    onChange={handleChange}
                    className="form-control"
                    disabled={loading}
                  >
                    <option value="">Select Role</option>
                    <option value="1">Admin</option>
                    <option value="2">Staff</option>
                  </select>
                  <small className="text-muted">A secure password setup link will be sent to the staff member's email</small>
                </div>

                {/* Submit Button */}
                <button type="submit" className="btn btn-primary" disabled={loading}>
                  {loading ? (
                    <>
                      <span
                        className="spinner-border spinner-border-sm me-2"
                        role="status"
                        aria-hidden="true"
                      ></span>
                      Adding...
                    </>
                  ) : (
                    "Add Staff"
                  )}
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default AddStaff;

