import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import { useMask } from "@react-input/mask";
import { countryCodes } from "../countryCodes";
import { BACKEND_URL } from '../config';



function NewCustomerForm() {
  const navigate = useNavigate();
  const [isChecked, setIsChecked] = useState(true);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [errors, setErrors] = useState({});

  const cellPhoneRef = useMask({
    mask: "(___) ___-____",
    replacement: { _: /\d/ },
  });

  const stripMask = (val) => (val ? val.replace(/\D/g, "") : "");

  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.first_name.trim()) {
      newErrors.first_name = "First name is required";
    }

    if (!formData.last_name.trim()) {
      newErrors.last_name = "Last name is required";
    }

    if (!formData.dob) {
      newErrors.dob = "Date of birth is required";
    } else {
      const dobDate = new Date(formData.dob);
      const today = new Date();
      if (dobDate > today) {
        newErrors.dob = "Date of birth cannot be in the future";
      }
    }

    if (!formData.email.trim()) {
      newErrors.email = "Email is required";
    } else if (!validateEmail(formData.email)) {
      newErrors.email = "Please enter a valid email address (e.g., name@example.com)";
    }

    const phoneDigits = stripMask(formData.cell_phone);
    if (!phoneDigits) {
      newErrors.cell_phone = "Phone number is required";
    } else if (phoneDigits.length !== 10) {
      newErrors.cell_phone = "Phone number must be exactly 10 digits";
    }

    if (!formData.address.trim()) {
      newErrors.address = "Address is required";
    }

    if (!formData.city.trim()) {
      newErrors.city = "City is required";
    }

    if (!formData.province.trim()) {
      newErrors.province = "Province is required";
    }

    if (!formData.postal_code.trim()) {
      newErrors.postal_code = "Postal code is required";
    }

    if (formData.signing_for_minor && minorList.length > 0) {
      minorList.forEach((minor, index) => {
        if (!minor.first_name.trim()) {
          newErrors[`minor_${index}_first_name`] = `Minor ${index + 1} first name is required`;
        }
        if (!minor.last_name.trim()) {
          newErrors[`minor_${index}_last_name`] = `Minor ${index + 1} last name is required`;
        }
        if (!minor.dob) {
          newErrors[`minor_${index}_dob`] = `Minor ${index + 1} date of birth is required`;
        } else {
          const minorDobDate = new Date(minor.dob);
          const today = new Date();
          if (minorDobDate > today) {
            newErrors[`minor_${index}_dob`] = `Minor ${index + 1} date of birth cannot be in the future`;
          }
        }
      });
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  }; // remove formatting

  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [countrySearch, setCountrySearch] = useState("");
  const dropdownRef = useRef(null);

  // Add this function to handle country selection
  const handleCountrySelect = (code) => {
    setFormData((prev) => ({
      ...prev,
      country_code: code,
    }));
    setIsDropdownOpen(false);
    setCountrySearch("");
  };

  // Filter countries based on search
  const filteredCountryCodes = countryCodes.filter((country) =>
    country.name.toLowerCase().includes(countrySearch.toLowerCase()) ||
    country.code.includes(countrySearch)
  );

  // Handle click outside and Escape key
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
        setCountrySearch("");
      }
    };

    const handleEscapeKey = (event) => {
      if (event.key === "Escape") {
        setIsDropdownOpen(false);
        setCountrySearch("");
      }
    };

    if (isDropdownOpen) {
      document.addEventListener("mousedown", handleClickOutside);
      document.addEventListener("keydown", handleEscapeKey);
    }

    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
      document.removeEventListener("keydown", handleEscapeKey);
    };
  }, [isDropdownOpen]);

  const [formData, setFormData] = useState({
    first_name: "",
    last_name: "",
    dob: "",
    address: "",
    city: "",
    province: "",
    postal_code: "",
    cell_phone: "",
    email: "",
    signing_for_minor: false,
    minors: [],
    country_code: "+1"
  });  

  const [minorList, setMinorList] = useState([]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleMinorChange = (index, field, value) => {
    const updated = [...minorList];
    updated[index][field] = value;
    setMinorList(updated);
  };

  const addMinor = () => {
    setMinorList([...minorList, { first_name: "", last_name: "", dob: "" }]);
  };

  const removeMinor = (index) => {
    const updated = [...minorList];
    updated.splice(index, 1);
    setMinorList(updated);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      const firstErrorKey = Object.keys(errors)[0];
      const firstErrorMessage = errors[firstErrorKey] || "Please correct the errors in the form";
      toast.error(firstErrorMessage);
      return;
    }

    setIsSubmitting(true);

    const cleanPhone = stripMask(formData.cell_phone);
    const phoneWithCode = `${formData.country_code}${cleanPhone}`;
    const fullData = {
      ...formData,
      cell_phone: cleanPhone,
      cc_cell_phone: phoneWithCode,
      minors: minorList,
      send_otp: isChecked,
    };

    try {
      await axios.post(`${BACKEND_URL}/api/waivers`, fullData);
      if (isChecked) {
        toast.success(`Customer created and OTP sent successfully.`);
        navigate("/opt-verified", {
          state: { phone: stripMask(formData.cell_phone), customerType: "new" },
        });
      } else {
        toast.success("Customer created successfully. Skipping OTP.");
        navigate("/signature", {
          state: { phone: stripMask(formData.cell_phone) },
        });
      }
    } catch (err) {
      if (err.response && err.response.data?.error) {
        toast.error(`${err.response.data.error}`);
      } else {
        console.error(err);
        toast.error("Error submitting form. Please try again.");
      }
    } finally {
      setIsSubmitting(false);
    }
  };


  return (
    <div className="container-fluid">
      <div className="container text-center">
        <div className="row">
          <div className="col-md-2">
            <div className="back-btn">
              <Link to="/">
                <img
                  className="img-fluid"
                  src="/assets/img/image 298.png"
                  alt="back-icon"
                />{" "}
                BACK
              </Link>
            </div>
          </div>

          <div className="col-12 col-md-8 col-xl-8">
            <div className="step-two step-three">
              <div className="logo">
                <img
                  className="img-fluid"
                  src="/assets/img/SKATE_AND_PLAY_V08_Full_Transparency (2) 1.png"
                  alt="logo"
                />
              </div>
            </div>
          </div>
        </div>

        <div className="row">
          <div className="col-md-12 col-xl-10 mx-auto">
            <h3 className="h5-heading">Your details </h3>
            <form onSubmit={handleSubmit}>
              <div className="info-table w-100">
                <table cellPadding="8" cellSpacing="0">
                  <tbody>
                    <tr>
                      <td>
                        First Name:<span className="required-star">*</span>
                        <br />
                        <input
                          type="text"
                          name="first_name"
                          value={formData.first_name}
                          onChange={handleChange}
                          className={`form-control ${errors.first_name ? 'is-invalid' : ''}`}
                        />
                        {errors.first_name && <small className="text-danger">{errors.first_name}</small>}
                      </td>
                      <td>
                        Last Name:<span className="required-star">*</span>
                        <br />
                        <input
                          type="text"
                          name="last_name"
                          value={formData.last_name}
                          onChange={handleChange}
                          className={`form-control ${errors.last_name ? 'is-invalid' : ''}`}
                        />
                        {errors.last_name && <small className="text-danger">{errors.last_name}</small>}
                      </td>
                      <td>
                        DOB:<span className="required-star">*</span>
                        <br />
                        <input
                          type="date"
                          name="dob"
                          value={formData.dob}
                          onChange={handleChange}
                          className={`form-control ${errors.dob ? 'is-invalid' : ''}`}
                        />
                        {errors.dob && <small className="text-danger">{errors.dob}</small>}
                      </td>
                    </tr>

                    <tr>
                      <td >
                        Address:<span className="required-star">*</span>
                        <br />
                        <input
                          type="text"
                          name="address"
                          value={formData.address}
                          onChange={handleChange}
                          className={`form-control ${errors.address ? 'is-invalid' : ''}`}
                        />
                        {errors.address && <small className="text-danger">{errors.address}</small>}
                      </td>
                      <td>
                        City:<span className="required-star">*</span>
                        <br />
                        <input
                          type="text"
                          name="city"
                          value={formData.city}
                          onChange={handleChange}
                          className={`form-control ${errors.city ? 'is-invalid' : ''}`}
                        />
                        {errors.city && <small className="text-danger">{errors.city}</small>}
                      </td>
                      <td>
                        Province:<span className="required-star">*</span>
                        <br />
                        <input
                          type="text"
                          name="province"
                          value={formData.province}
                          onChange={handleChange}
                          className={`form-control ${errors.province ? 'is-invalid' : ''}`}
                        />
                        {errors.province && <small className="text-danger">{errors.province}</small>}
                      </td>

                    </tr>

                    <tr>

                          <td>
                        Postal Code:<span className="required-star">*</span>
                        <br />
                        <input
                          type="text"
                          name="postal_code"
                          value={formData.postal_code}
                          onChange={handleChange}
                          className={`form-control ${errors.postal_code ? 'is-invalid' : ''}`}
                        />
                        {errors.postal_code && <small className="text-danger">{errors.postal_code}</small>}
                      </td>
                      {/* <td>
                        Cell Phone:<span className="required-star">*</span>
                        <br />
                        <input
                          ref={cellPhoneRef}
                          type="tel"
                          name="cell_phone"
                          value={formData.cell_phone}
                          onChange={handleChange}
                          className="form-control"
                          required
                        />
                      </td> */}

                      <td>
                        Cell Phone:<span className="required-star">*</span>
                        <br />
                        <div className="phone-input-group">
                          <div className="custom-dropdown" style={{ position: 'relative' }} ref={dropdownRef}>
                            <div
                              className="form-select"
                              onClick={() => setIsDropdownOpen(!isDropdownOpen)}
                              style={{ cursor: 'pointer' }}
                            >
                              {formData.country_code}
                            </div>
                            {isDropdownOpen && (
                              <div
                                className="dropdown-menu show"
                                style={{
                                  position: 'absolute',                              
                                  maxHeight: '250px',
                                  overflowY: 'auto',
                                  zIndex: 1000,
                                  width: '300px'
                                }}
                              >
                                <div style={{ position: 'sticky', top: 0, background: 'white', padding: '8px', borderBottom: '1px solid #ddd' }}>
                                  <input
                                    type="text"
                                    placeholder="Search country or code..."
                                    value={countrySearch}
                                    onChange={(e) => setCountrySearch(e.target.value)}
                                    className="form-control form-control-sm"
                                    onClick={(e) => e.stopPropagation()}
                                    autoFocus
                                  />
                                </div>
                                {filteredCountryCodes.length > 0 ? (
                                  filteredCountryCodes.map((c, index) => (
                                    <div
                                      key={index}
                                      className="dropdown-item"
                                      onClick={() => handleCountrySelect(c.code)}
                                      style={{ cursor: 'pointer' }}
                                    >
                                      {c.code} - {c.name}
                                    </div>
                                  ))
                                ) : (
                                  <div className="dropdown-item" style={{ color: '#999' }}>
                                    No countries found
                                  </div>
                                )}
                              </div>
                            )}
                          </div>

                          <input
                            ref={cellPhoneRef}
                            type="tel"
                            name="cell_phone"
                            value={formData.cell_phone}
                            onChange={handleChange}
                            className={`form-control ${errors.cell_phone ? 'is-invalid' : ''}`}
                          />
                        </div>
                        {errors.cell_phone && <small className="text-danger">{errors.cell_phone}</small>}
                      </td>

                      <td >
                        Email:<span className="required-star">*</span>
                        <br />
                        <input
                          type="email"
                          name="email"
                          value={formData.email}
                          onChange={handleChange}
                          className={`form-control ${errors.email ? 'is-invalid' : ''}`}
                        />
                        {errors.email && <small className="text-danger">{errors.email}</small>}
                      </td>
                    </tr>
                  </tbody>
                </table>
              </div>

              <div className="text-start my-4">
                <div className="d-flex align-items-center custom-radio-wrapper">
                  <h5>I'm signing on behalf of a minor or dependent</h5>
                  <label className="ms-3">
                    <input
                      type="radio"
                      name="signing_for_minor"
                      checked={formData.signing_for_minor === true}
                      onChange={() => {
                        setFormData((p) => ({ ...p, signing_for_minor: true }));
                        if (minorList.length === 0) {
                          setMinorList([
                            { first_name: "", last_name: "", dob: "" },
                          ]);
                        }
                      }}
                    />{" "}
                    Yes
                  </label>
                  <label className="ms-3">
                    <input
                      type="radio"
                      name="signing_for_minor"
                      checked={formData.signing_for_minor === false}
                      onChange={() => {
                        setFormData((p) => ({
                          ...p,
                          signing_for_minor: false,
                        }));
                        setMinorList([]);
                      }}
                    />{" "}
                    No
                  </label>
                </div>
              </div>

              {formData.signing_for_minor && (
                <>
                  {minorList.map((minor, index) => (
                    <div key={index} className="mb-3">
                      <div className="minor-group d-flex gap-2">
                        <div className="flex-fill">
                          <input
                            type="text"
                            className={`form-control ${errors[`minor_${index}_first_name`] ? 'is-invalid' : ''}`}
                            placeholder="First Name"
                            value={minor.first_name}
                            onChange={(e) =>
                              handleMinorChange(index, "first_name", e.target.value)
                            }
                          />
                          {errors[`minor_${index}_first_name`] && (
                            <small className="text-danger d-block mt-1">{errors[`minor_${index}_first_name`]}</small>
                          )}
                        </div>
                        <div className="flex-fill">
                          <input
                            type="text"
                            className={`form-control ${errors[`minor_${index}_last_name`] ? 'is-invalid' : ''}`}
                            placeholder="Last Name"
                            value={minor.last_name}
                            onChange={(e) =>
                              handleMinorChange(index, "last_name", e.target.value)
                            }
                          />
                          {errors[`minor_${index}_last_name`] && (
                            <small className="text-danger d-block mt-1">{errors[`minor_${index}_last_name`]}</small>
                          )}
                        </div>
                        <div className="flex-fill">
                          <input
                            type="date"
                            className={`form-control ${errors[`minor_${index}_dob`] ? 'is-invalid' : ''}`}
                            value={minor.dob}
                            onChange={(e) =>
                              handleMinorChange(index, "dob", e.target.value)
                            }
                          />
                          {errors[`minor_${index}_dob`] && (
                            <small className="text-danger d-block mt-1">{errors[`minor_${index}_dob`]}</small>
                          )}
                        </div>
                        <button
                          type="button"
                          className="btn btn-danger"
                          onClick={() => removeMinor(index)}
                        >
                          Remove
                        </button>
                      </div>
                    </div>
                  ))}
                  <button
                    type="button"
                    onClick={addMinor}
                    className="btn btn-primary my-2"
                  >
                    Add another minor
                  </button>
                </>
              )}

              <div className="my-4">
                <div className="confirm-box text-start">
                  <label className="custom-checkbox-wrapper">
                    <input
                      type="checkbox"
                      className="custom-checkbox"
                      checked={isChecked}
                      onChange={() => setIsChecked((prev) => !prev)}
                    />
                    <span className="custom-checkbox-label">
                      <h5>
                        Save time on your next visit! Use your phone number as a
                        reference for future waivers. Just check the box and
                        receive a quick validation text.
                      </h5>
                    </span>
                  </label>
                </div>
              </div>

              <div className="buttons mb-5">
                <button
                  type="submit"
                  className="btn btn-primary w-25"
                  disabled={isSubmitting}
                >
                  {isSubmitting ? (
                    <>
                      <span
                        className="spinner-border spinner-border-sm me-2"
                        role="status"
                        aria-hidden="true"
                      ></span>
                      Submitting...
                    </>
                  ) : (
                    "Next"
                  )}
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default NewCustomerForm;

src/pages/ConfirmCustomerInfo.js.content:
import React, { useEffect, useState } from "react";
import { useLocation, useNavigate , Link} from "react-router-dom";
import axios from "axios";
import { toast } from 'react-toastify';
import { BACKEND_URL } from '../config';

function ConfirmCustomerInfo() {
  const location = useLocation();
  const navigate = useNavigate();
  const phone = location.state?.phone;
  const customerId = location.state?.customerId;
  const isReturning = location.state?.isReturning || false;

  const [formData, setFormData] = useState(null);
  const [minorList, setMinorList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [updating, setUpdating] = useState(false);


  useEffect(() => {
    if (phone) {
      setLoading(true);

      const endpoint = customerId 
        ? `${BACKEND_URL}/api/waivers/customer-info-by-id?customerId=${customerId}`
        : `${BACKEND_URL}/api/waivers/customer-info?phone=${phone}`;

      axios
        .get(endpoint)
        .then((res) => {
          const data = res.data.customer;


            // âœ… Convert numbers into masked format if exists
          const formatPhone = (num) => {
            if (!num) return "";
            const digits = num.replace(/\D/g, "").slice(0, 10);
            if (digits.length === 10) {
              return `(${digits.slice(0, 3)}) ${digits.slice(3, 6)}-${digits.slice(6)}`;
            }
            return digits;
          };

          data.home_phone = formatPhone(data.home_phone);
          data.cell_phone = formatPhone(data.cell_phone);
          data.work_phone = formatPhone(data.work_phone);


          data.can_email = data.can_email === 1 || data.can_email === "1";
          setFormData(data);

          if (res.data.minors) {
            const minorsWithFlags = res.data.minors.map((minor) => ({
              ...minor,
              dob: minor.dob ? new Date(minor.dob).toISOString().split("T")[0] : "",
              checked: minor.status === 1,
              isNew: false,
            }));
            setMinorList(minorsWithFlags);
          }
        })
        .catch((err) => {
          console.error(err);
          toast.error(err?.response?.data?.message || "Failed to fetch customer info.");
        })
        .finally(() => {
          setLoading(false);
        });
    }
  }, [phone, customerId]);

  // const handleChange = (e) => {
  //   const { name, value, type } = e.target;
  //   setFormData((prev) => ({
  //     ...prev,
  //     [name]: type === "number" ? Number(value) : value,
  //   }));
  // };

   const handleChange = (e) => {
    const { name, value } = e.target;

    // âœ… If it's a phone â†’ only store masked for UI
    if ([ "cell_phone"].includes(name)) {
      setFormData((prev) => ({
        ...prev,
        [name]: value,
      }));
    } else {
      setFormData((prev) => ({
        ...prev,
        [name]: value,
      }));
    }
  };

  const goToSignature = async () => {
    // Validate new minors have all required fields
    const newMinors = minorList.filter(minor => minor.isNew);
    const invalidNewMinors = newMinors.filter(
      minor => !minor.first_name?.trim() || !minor.last_name?.trim() || !minor.dob
    );

    if (invalidNewMinors.length > 0) {
      toast.error("Please complete all fields (first name, last name, and date of birth) for all new minors before continuing.");
      return;
    }

    // Validate dates are not in the future
    const minorsWithFutureDOB = newMinors.filter(minor => {
      if (!minor.dob) return false;
      const dobDate = new Date(minor.dob);
      const today = new Date();
      return dobDate > today;
    });

    if (minorsWithFutureDOB.length > 0) {
      toast.error("Date of birth cannot be in the future for any minor.");
      return;
    }

    setUpdating(true);
    try {
       const stripMask = (val) => (val ? val.replace(/\D/g, "") : "");
      const updatedData = {
        ...formData,

        cell_phone: stripMask(formData.cell_phone),

        minors: minorList.map((minor) => ({
          id: minor.id,
          first_name: minor.first_name,
          last_name: minor.last_name,
          dob: minor.dob,
          isNew: minor.isNew,
          checked: minor.checked,
        })),
      };

      if (!isReturning) {
        await axios.post(`${BACKEND_URL}/api/waivers/update-customer`, updatedData);
      }

      navigate("/signature", { 
        state: { 
          phone, 
          formData: updatedData,
          customerId: formData.id,
          isReturning
        } 
      });
    } catch (err) {
      console.error("Error updating customer:", err);
      toast.error("Failed to update customer info.");
    } finally {
      setUpdating(false);
    }
  };
  if (loading || !formData) {
    return <div className="text-center mt-5">Loading customer info...</div>;
  }

  return (
    <div className="container-fluid">
      <div className="container text-center">
        <div className="row">
          <div className="col-md-2">
            <div className="back-btn">
              <Link to={isReturning ? "/user-dashboard" : "/existing-customer"} state={{ phone }}>
                <img className="img-fluid" src="/assets/img/image 298.png" alt="back-icon" /> BACK
              </Link>
            </div>
          </div>
          <div className="col-12 col-md-8 col-xl-8">
            <div className="step-two step-three">
              <div className="logo">
                <img className="img-fluid" src="/assets/img/SKATE_AND_PLAY_V08_Full_Transparency (2) 1.png" alt="logo" />
              </div>
            </div>
          </div>
        </div>

        <div className="row">
          <div className="col-md-12 col-xl-10 mx-auto">
            <h3 className="h5-heading">Please confirm that all information below is still accurate</h3>

            <form>
              <div className="info-table w-100">
