                          setMinorList(updated);
                        }}
                      />
                    </label>
                    <input
                      type="text"
                      className="form-control"
                      placeholder="First Name"
                      value={minor.first_name}
                      onChange={(e) => {
                        const updated = [...minorList];
                        updated[index].first_name = e.target.value;
                        setMinorList(updated);
                      }}
                      readOnly={!minor.isNew}
                      style={!minor.isNew ? { backgroundColor: '#e9ecef', cursor: 'not-allowed' } : {}}
                    />
                    <input
                      type="text"
                      className="form-control"
                        placeholder="Last Name"
                      value={minor.last_name}
                      onChange={(e) => {
                        const updated = [...minorList];
                        updated[index].last_name = e.target.value;
                        setMinorList(updated);
                      }}
                      readOnly={!minor.isNew}
                      style={!minor.isNew ? { backgroundColor: '#e9ecef', cursor: 'not-allowed' } : {}}
                    />
                    <input
                      type="date"
                      className="form-control"
                      value={minor.dob}
                      onChange={(e) => {
                        const updated = [...minorList];
                        updated[index].dob = e.target.value;
                        setMinorList(updated);
                      }}
                      readOnly={!minor.isNew}
                      style={!minor.isNew ? { backgroundColor: '#e9ecef', cursor: 'not-allowed' } : {}}
                    />
                    {minor.isNew && (
                      <button
                        type="button"
                        className="btn btn-danger"
                        onClick={() => {
                          const updated = [...minorList];
                          updated.splice(index, 1);
                          setMinorList(updated);
                        }}
                      >
                        Remove
                      </button>
                    )}
                  </div>
                ))}

                <div className="buttons mt-3">
                  <button
                    type="button"
                    className="btn btn-secondary me-2"
                    onClick={() =>
                      setMinorList([
                        ...minorList,
                        { first_name: "", last_name: "", dob: "", checked: true, isNew: true },
                      ])
                    }
                  >
                    Add another minor
                  </button>
                  <span className="or-text mx-2">or</span>
                  <button type="button" className="btn btn-primary" onClick={goToSignature} disabled={updating}>
                    {updating ? "Confirming..." : "Confirm"}
                  </button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ConfirmCustomerInfo;

src/pages/admin/ResetPassword.js.content:
import React, { useEffect, useState } from "react";
import { useSearchParams, useNavigate } from "react-router-dom";
import axios from "../../utils/axios";
import { toast } from "react-toastify";
import { BACKEND_URL } from '../../config';

function ResetPasswordForm() {
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();

  const [form, setForm] = useState({
    password: "",
    confirmPassword: "",
  });

  const [token, setToken] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const resetToken = searchParams.get("token");

    if (resetToken) {
      setToken(resetToken);
    } else {
      toast.error("Invalid reset link.");
    }
  }, [searchParams]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (form.password !== form.confirmPassword) {
      return toast.error("Passwords do not match");
    }

    if (form.password.length < 6) {
      return toast.error("Password must be at least 6 characters long");
    }

    setLoading(true);

    try {
      const response = await axios.post(`${BACKEND_URL}/api/staff/update-password`, {
        token,
        newPassword: form.password,
      });

      toast.success(response.data.message || "Password updated successfully!");

      localStorage.setItem("token", response.data.token);
      localStorage.setItem("staff", JSON.stringify(response.data.staff));

      navigate("/admin/home");
    } catch (err) {
      toast.error(err.response?.data?.error || err.response?.data?.message || "Unable to reset your password. Please try again or request a new reset link.");
    } finally {
      setLoading(false);
    }
  };

  return (
        <div className="container-fluid">
      <div className="container">
        <div className="row">
          <div className="col-12 col-md-6 col-xl-4 mx-auto">
            <div className="login-container">
              <div className="logo text-center mb-4">
                <img
                  className="img-fluid"
                  src="/assets/img/SKATE_AND_PLAY_V08_Full_Transparency (2) 1.png"
                  alt="logo"
                />
              </div>
      <form className="mt-4" onSubmit={handleSubmit}>
        <div className="mb-3">
          <label>New Password</label>
          <input
            type="password"
            className="form-control"
            value={form.password}
            onChange={(e) => setForm({ ...form, password: e.target.value })}
            required
          />
        </div>
        <div className="mb-3">
          <label>Confirm Password</label>
          <input
            type="password"
            className="form-control"
            value={form.confirmPassword}
            onChange={(e) => setForm({ ...form, confirmPassword: e.target.value })}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary w-100" disabled={loading}>
          {loading ? "Updating..." : "Update Password"}
        </button>
      </form>
          </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ResetPasswordForm;

