const cron = require("node-cron");
const db = require("./db/connection");
const sendRatingEmail = require("./utils/sendRatingEmail");
const sendRatingSMS = require("./utils/sendRatingSMS");

// Run every hour at 0 minutes
cron.schedule("0 * * * *", async () => {
  console.log("ðŸ” Checking for waivers that need rating messages...");

  try {
    const [waivers] = await db.query(`
      SELECT wf.id AS waiver_id, wf.*, c.* 
      FROM waiver_forms wf
      JOIN customers c ON wf.user_id = c.id
      WHERE wf.signed_at IS NOT NULL
      AND TIMESTAMPDIFF(HOUR, wf.signed_at, UTC_TIMESTAMP()) >= 3
      AND (wf.rating_email_sent = 0 OR wf.rating_sms_sent = 0)
    `);

    console.log("Found waivers:", waivers.length);

    if (waivers.length === 0) {
      console.log("âœ… No waivers pending for rating messages.");
      return;
    }

    for (let waiver of waivers) {
      // ðŸ“§ Email sending
      if (!waiver.rating_email_sent) {
        try {
          if (waiver.email && waiver.email.trim() !== "") {
            await sendRatingEmail(waiver);
            await db.query(
              `UPDATE waiver_forms SET rating_email_sent = 1 WHERE id = ?`,
              [waiver.waiver_id]
            );
            console.log(`ðŸ“§ Email sent to ${waiver.email}`);
          } else {
            await db.query(
              `UPDATE waiver_forms SET rating_email_sent = 2 WHERE id = ?`,
              [waiver.waiver_id]
            );
            console.warn(
              `âš ï¸ No valid email for waiver ID ${waiver.waiver_id}, marked as failed.`
            );
          }
        } catch (err) {
          console.error(
            `âŒ Email failed for waiver ID ${waiver.waiver_id}:`,
            err.message
          );
          await db.query(
            `UPDATE waiver_forms SET rating_email_sent = 2 WHERE id = ?`,
            [waiver.waiver_id]
          );
        }
      }

      // ðŸ“² SMS sending
      if (!waiver.rating_sms_sent) {
        try {
          if (waiver.cell_phone && waiver.cell_phone.trim() !== "") {
            await sendRatingSMS(waiver);
            await db.query(
              `UPDATE waiver_forms SET rating_sms_sent = 1 WHERE id = ?`,
              [waiver.waiver_id]
            );
            console.log(
              `ðŸ“² SMS sent to ${waiver.cell_phone} (waiver ID ${waiver.waiver_id})`
            );
          } else {
            await db.query(
              `UPDATE waiver_forms SET rating_sms_sent = 2 WHERE id = ?`,
              [waiver.waiver_id]
            );
            console.warn(
              `âš ï¸ No valid phone for waiver ID ${waiver.waiver_id}, marked as failed.`
            );
          }
        } catch (err) {
          console.error(
            `âŒ SMS failed for waiver ID ${waiver.waiver_id}:`,
            err.message
          );
          await db.query(
            `UPDATE waiver_forms SET rating_sms_sent = 2 WHERE id = ?`,
            [waiver.waiver_id]
          );
        }
      }
    }
  } catch (err) {
    console.error("ðŸš¨ Query failed:", err);
  }
});

backend/controllers/feedbackController.j.content:
const db = require('../config/database');
const nodemailer = require('nodemailer');

/**
 * Submits customer feedback and sends email notification to admin
 * 
 * Index suggestions:
 * - CREATE INDEX idx_feedback_user_id ON feedback(user_id)
 * - CREATE INDEX idx_feedback_created_at ON feedback(created_at)
 * - CREATE INDEX idx_feedback_rating ON feedback(rating)
 */
const sendFeedback = async (req, res) => {
  try {
    const { user_id, rating, message, issue, staff_name } = req.body;

    // Validate required fields
    if (!user_id || !rating) {
      return res.status(400).json({ 
        error: 'User ID and rating are required' 
      });
    }

    // Validate rating range (1-5)
    if (rating < 1 || rating > 5) {
      return res.status(400).json({ 
        error: 'Rating must be between 1 and 5' 
      });
    }

    // Get customer info
    const [customers] = await db.query(
      'SELECT first_name, last_name, email FROM customers WHERE id = ?',
      [user_id]
    );

    if (customers.length === 0) {
      return res.status(404).json({ 
        error: 'Customer not found' 
      });
    }

    const customer = customers[0];

    // Insert feedback
    await db.query(
      'INSERT INTO feedback (user_id, rating, message, issue, staff_name) VALUES (?, ?, ?, ?, ?)',
      [user_id, rating, message || null, issue || null, staff_name || null]
    );

    // Send email notification to admin (if message, issue, or staff_name provided)
    if (message || issue || staff_name) {
      try {
        const transporter = nodemailer.createTransport({
          host: process.env.SMTP_HOST,
          port: process.env.SMTP_PORT,
          secure: true,
          auth: {
            user: process.env.SMTP_USER,
            pass: process.env.SMTP_PASS
          },
          tls: { rejectUnauthorized: false }
        });

        const htmlTemplate = `
          <!DOCTYPE html>
          <html>
          <head>
            <meta charset="UTF-8">
            <title>Customer Feedback</title>
          </head>
          <body style="font-family: Arial, sans-serif; background-color: #f9f9f9; padding: 0; margin: 0;">
            <table width="100%" cellpadding="0" cellspacing="0">
              <tr>
                <td align="center" style="padding: 40px 0;">
                  <table width="600" cellpadding="0" cellspacing="0" style="background-color: #ffffff; border-radius: 10px; box-shadow: 0 0 10px rgba(0,0,0,0.1);">
                    <tr>
                      <td style="background-color: #002244; color: white; padding: 20px; text-align: center;">
                        <h2>Customer Feedback</h2>
                      </td>
                    </tr>
                    <tr>
                      <td style="padding: 30px;">
                        <p><strong>Customer:</strong> ${customer.first_name} ${customer.last_name}</p>
                        <p><strong>Email:</strong> ${customer.email}</p>
                        <p><strong>Rating:</strong> ${rating} / 5 â­</p>
                        <p><strong>Feedback:</strong></p>
                        <p style="background-color: #f5f5f5; padding: 15px; border-radius: 5px; color: #333;">Issue: ${issue || "N/A"}</p>
                        <p style="background-color: #f5f5f5; padding: 15px; border-radius: 5px; color: #333;">Staff Name: ${staff_name || "N/A"}</p>
                        <p style="background-color: #f5f5f5; padding: 15px; border-radius: 5px; color: #333;">Comments: ${message || "N/A"}</p>
                      </td>
                    </tr>
                    <tr>
                      <td style="text-align: center; background-color: #f1f1f1; padding: 10px; font-size: 12px; color: #888;">
                        &copy; 2025 Skate & Play. All rights reserved.
                      </td>
                    </tr>
                  </table>
                </td>
              </tr>
            </table>
          </body>
          </html>
        `;

        await transporter.sendMail({
          from: process.env.SMTP_USER,
          to: process.env.SMTP_USER, 
          subject: `Customer Feedback - ${customer.first_name} ${customer.last_name}`,
          html: htmlTemplate
        });

        console.log(`Feedback email sent for customer ${user_id}`);
      } catch (emailError) {
        console.error('Feedback email error:', emailError.message);
        // Don't fail the request if email fails
      }
    }

    res.json({ 
      success: true, 
      message: 'Feedback submitted successfully' 
    });
  } catch (error) {
    const errorId = `ERR_${Date.now()}`;
    console.error(`[${errorId}] Error sending feedback:`, {
      message: error.message,
      userId: req.body.user_id
    });

    res.status(500).json({ 
      error: 'Failed to submit feedback',
      errorId 
    });
  }
};

/**
 * Gets customer information for rating page
 */
const getRatingInfo = async (req, res) => {
  try {
    const { userId } = req.params;

    // Validate user ID
    if (!userId || isNaN(userId)) {
      return res.status(400).json({ 
        error: 'Valid user ID is required' 
      });
    }

    const [customers] = await db.query(
      'SELECT first_name, last_name, email FROM customers WHERE id = ?',
      [userId]
    );

    if (customers.length === 0) {
      return res.status(404).json({ 
        error: 'Customer not found' 
      });
    }

    res.json(customers[0]);
  } catch (error) {
    const errorId = `ERR_${Date.now()}`;
    console.error(`[${errorId}] Error fetching rating info:`, {
      message: error.message,
      userId: req.params.userId
    });

    res.status(500).json({ 
      error: 'Failed to fetch customer information',
      errorId 
    });
  }
};

/**
 * Gets all feedback with customer information
 * Optimized with single JOIN query
 */
const getAllFeedback = async (req, res) => {
  try {
    const [feedback] = await db.query(`
      SELECT 
        f.id,
        f.user_id,
        f.rating,
        f.message,
        f.issue,
        f.staff_name,
        f.created_at,
        c.first_name,
        c.last_name,
        c.email,
        c.cell_phone
      FROM feedback f
      JOIN customers c ON f.user_id = c.id
      ORDER BY f.created_at DESC
    `);

    res.json(feedback);
  } catch (error) {
    const errorId = `ERR_${Date.now()}`;
    console.error(`[${errorId}] Error fetching feedback:`, {
      message: error.message
    });

    res.status(500).json({ 
      error: 'Failed to fetch feedback',
      errorId 
    });
  }
};

module.exports = {
  sendFeedback,
  getRatingInfo,
  getAllFeedback
};

backend/controllers/staffController.js.content:
const db = require('../config/database');
const bcrypt = require('bcrypt');
const jwt = require('jsonwebtoken');
const nodemailer = require('nodemailer');

/**
 * Authenticates staff member and returns JWT token
 * 
 * Index suggestions:
 * - CREATE INDEX idx_staff_email ON staff(email)
 * - CREATE INDEX idx_staff_email_status ON staff(email, status)
 */
const login = async (req, res) => {
  try {
    const { email, password } = req.body;

    // Validate required fields
    if (!email || !password) {
      return res.status(400).json({ 
        error: 'Email and password are required' 
      });
    }

    // Validate email format
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
      return res.status(400).json({ 
        error: 'Invalid email format' 
      });
    }

    // Check if JWT_SECRET is configured
    if (!process.env.JWT_SECRET) {
      const errorId = `ERR_${Date.now()}`;
      console.error(`[${errorId}] JWT_SECRET not configured`);
      return res.status(500).json({ 
        error: 'Server configuration error',
        errorId 
      });
    }

    const [staff] = await db.query(
      'SELECT * FROM staff WHERE email = ?',
      [email]
    );

    // Use same error message for security (don't reveal if email exists)
    if (staff.length === 0) {
      return res.status(401).json({ 
        error: 'Invalid email or password' 
      });
    }

    const user = staff[0];

    // Check if account is active
    if (user.status !== 1) {
      return res.status(403).json({ 
        error: 'Account is inactive. Please contact administrator' 
      });
    }

    // Verify password
    const isValidPassword = await bcrypt.compare(password, user.password);

    if (!isValidPassword) {
      return res.status(401).json({ 
        error: 'Invalid email or password' 
      });
    }

    // Generate JWT token
    const token = jwt.sign(
      { id: user.id, email: user.email, role: user.role },
      process.env.JWT_SECRET,
      { expiresIn: '24h' }
    );

    res.json({
      success: true,
      message: 'Login successful',
      token,
      staff: {
        id: user.id,
        name: user.name,
        email: user.email,
        role: user.role,
        profile_image: user.profile_image
      }
    });
  } catch (error) {
    const errorId = `ERR_${Date.now()}`;
    console.error(`[${errorId}] Error during login:`, {
      message: error.message,
      email: req.body.email
    });

    res.status(500).json({ 
      error: 'Login failed',
      errorId 
    });
  }
};

/**
 * Initiates password reset for staff member
 * Generates secure token and sends reset link via email
 */
const forgetPassword = async (req, res) => {
  try {
    const { email } = req.body;

    // Validate email
    if (!email) {
      return res.status(400).json({ 
        error: 'Please enter your email address' 
      });
    }

    // Validate email format
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
      return res.status(400).json({ 
        error: 'Please enter a valid email address' 
      });
    }

    const [staff] = await db.query(
      'SELECT id, name FROM staff WHERE email = ?',
      [email]
    );

    if (staff.length === 0) {
      return res.status(404).json({ 
        error: 'No account found with this email address' 
      });
    }

    const user = staff[0];

    // Generate secure random token
    const crypto = require('crypto');
    const resetToken = crypto.randomBytes(32).toString('hex');

    // Set token expiry to 24 hours from now
    const tokenExpiry = new Date();
    tokenExpiry.setHours(tokenExpiry.getHours() + 24);

    // Update staff with reset token
    await db.query(
      'UPDATE staff SET reset_token = ?, token_expiry = ? WHERE id = ?',
      [resetToken, tokenExpiry, user.id]
    );

    const resetBase = process.env.REACT_LINK_BASE || 'http://localhost:3000';
    const resetLink = `${resetBase}/admin/reset-password?token=${resetToken}`;

    console.log('ðŸ” Password reset requested for:', email);
    console.log('ðŸ”— Reset link:', resetLink);
    console.log('â° Token expires:', tokenExpiry.toISOString());

    // Send password reset email
    const transporter = nodemailer.createTransport({
      host: process.env.SMTP_HOST,
      port: process.env.SMTP_PORT,
      secure: true,
      auth: {
        user: process.env.SMTP_USER,
        pass: process.env.SMTP_PASS
      },
      tls: { rejectUnauthorized: false }
    });

    const htmlTemplate = `
      <!DOCTYPE html>
      <html>
      <head>
        <meta charset="UTF-8">
        <title>Reset Your Admin Password</title>
      </head>
      <body style="font-family: Arial, sans-serif; background-color: #f9f9f9; padding: 0; margin: 0;">
        <table width="100%" cellpadding="0" cellspacing="0">
          <tr>
            <td align="center" style="padding: 40px 0;">
              <table width="600" cellpadding="0" cellspacing="0" style="background-color: #fff; border-radius: 10px; box-shadow: 0 0 10px rgba(0,0,0,0.1);">
                <tr>
                  <td style="background-color: #002244; color: white; padding: 30px 20px; text-align: center;">
                    <img src="${resetBase}/assets/img/SKATE_AND_PLAY_V08_Full_Transparency (2) 1.png" alt="Skate & Play Logo" style="max-width: 200px; height: auto; margin-bottom: 10px;" />
                    <h2 style="margin: 10px 0 0 0;">Admin Portal</h2>
                  </td>
                </tr>
                <tr>
                  <td style="padding: 30px;">
                    <h3 style="color: #002244; margin-top: 0;">Password Reset Request</h3>
                    <p>Hi ${user.name || "Admin"},</p>
