      [email, id]
    );

    if (existing.length > 0) {
      return res.status(409).json({ 
        error: 'Email already exists' 
      });
    }

    // Build update query dynamically based on whether image was uploaded
    let query = 'UPDATE staff SET name = ?, email = ?';
    const params = [name, email];

    if (req.file) {
