h1,
h2,
h3,
h4,
h5,
h6 {
  margin: 0;
  padding: 0;
}


p {
  margin: 0;
  padding: 0;
}

body {
    font-family: "Poppins", sans-serif;
}

a {
  list-style: none;
  text-decoration: none;
}

.logo-img {
  width: 60%;
  margin: auto;
  margin-top: 100px;
