import React, { useEffect, useState } from "react";
import axios from "axios";
import $ from "jquery";

// DataTables core & CSS
import "datatables.net-dt/js/dataTables.dataTables";
import "datatables.net-dt/css/dataTables.dataTables.css";

import "datatables.net-responsive-dt/js/responsive.dataTables";
import "datatables.net-responsive-dt/css/responsive.dataTables.css";

import Header from "./components/header";
import { convertToEST } from "../../utils/time";

const AdminFeedbackPage = () => {
  const [feedbackList, setFeedbackList] = useState([]);
  const [loading, setLoading] = useState(true);
  const BACKEND_URL =
    process.env.REACT_APP_BACKEND_URL || "http://localhost:5000";

  // Escape HTML safely for tooltip
  const escapeHtml = (str) =>
    String(str).replace(/[&<>"']/g, (s) =>
      ({ "&": "&amp;", "<": "&lt;", ">": "&gt;", '"': "&quot;", "'": "&#39;" }[
        s
      ])
    );

  // Fetch feedback from backend
  useEffect(() => {
    (async () => {
      try {
        const { data } = await axios.get(
          `${BACKEND_URL}/api/waivers/getfeedback`
        );
        const sorted = data.sort(
          (a, b) => new Date(b.created_at) - new Date(a.created_at)
        );
        console.log("Feedback data:", sorted); // Debug
        setFeedbackList(sorted);
      } catch (err) {
        console.error("Failed to fetch feedback", err);
      } finally {
        setLoading(false);
      }
    })();
  }, [BACKEND_URL]);

  // Initialize DataTable after feedback loads
  useEffect(() => {
    if (loading || feedbackList.length === 0) return;

    if ($.fn.DataTable.isDataTable("#feedbackTable")) {
      $("#feedbackTable").DataTable().destroy();
    }

    setTimeout(() => {
      const dt = $("#feedbackTable").DataTable({
        data: feedbackList,
        responsive: true,
        paging: true,
        searching: true,
        ordering: true,
        autoWidth: false,
        columns: [
          {
            data: null,
            render: (data, type, row, meta) => meta.row + 1, // row index
          },
          {
            data: null,
            render: (row) => `${row.first_name || ""} ${row.last_name || ""}`,
          },
          { data: "rating", defaultContent: "-" },
          { data: "issue", defaultContent: "-" },
          { data: "staff_name", defaultContent: "-" },
          // {
          //   data: "message",
          //   render: (data, type) => {
          //     if (!data) return "-";
          //     if (type !== "display") return data;

          //     const max = 80;
          //     const full = escapeHtml(data);
          //     const shown =
          //       data.length > max ? escapeHtml(data.slice(0, max)) + "…" : full;

          //     return `<span title="${full}">${shown}</span>`;
          //   },
          // },
            {
                data: "message",
                render: (data, type) => {
                  if (!data) return "-";
                  if (type !== "display") return data;

                  const full = escapeHtml(data);
                  return `<span title="${full}">${full}</span>`; // ✅ show everything
                },
              },
            {
            data: "created_at",
            render: (data) => (data ? convertToEST(data) : "-"),
          },
        ],
      });

      $(".dt-search input[type='search']").attr(
        "placeholder",
        "Search feedback..."
      );
    }, 0);
  }, [loading, feedbackList]);

  return (
    <>
      <Header />
      <div className="container mt-5">
        <h5 className="h5-heading my-3 pb-3">Customer Feedback</h5>

        {loading ? (
          <p>Loading...</p>
        ) : (
          <table
            id="feedbackTable"
            className="display responsive"
            style={{ width: "100%" }}
          >
            <thead>
              <tr>
                <th>#</th>
                <th>Customer</th>
                <th>Rating</th>
                <th>Issue</th>
                <th>Staff Name</th>
                <th>Message</th>
                <th>Date</th>
              </tr>
            </thead>
            <tbody />
          </table>
        )}
      </div>
    </>
  );
};

export default AdminFeedbackPage;

src/pages/admin/AddStaff.js.content:
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";
import Header from "./components/header";

function AddStaff() {
    const navigate = useNavigate();
  const [form, setForm] = useState({ name: "", email: "", role: "" });
  const [loading, setLoading] = useState(false); // Spinner state
  const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || "http://localhost:5000";

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.name || !form.email || !form.role) {
      return toast.error("All fields are required");
    }

    setLoading(true); // Start spinner

    try {
      const res = await axios.post(`${BACKEND_URL}/api/staff/addstaff`, form);
      toast.success(res.data.message || "Staff added successfully");
       navigate("/admin/staff-list");
      setForm({ name: "", email: "", role: "" });
    } catch (error) {
      toast.error(error.response?.data?.error || "Failed to add staff");
    } finally {
      setLoading(false); // Stop spinner
    }
  };

  return (
    <>
      <Header />
      <div className="container">
        <div className="row">
          <div className="col-12 col-xl-7 col-md-10 mx-auto my-5">
            <div className="client-profile">
              <div>
                <h5 className="h5-heading">Add New Staff</h5>
                <div>
                  <img
                    className="img-fluid my-3"
                    src="/assets/img/Vector.png"
                    alt="profile-img"
                  />
                </div>
              </div>

              <form onSubmit={handleSubmit} className="add-staff-input">
                {/* Name Field */}
                <div className="mb-3">
                  <label className="form-label">Name</label>
                  <input
                    type="text"
                    name="name"
                    value={form.name}
                    onChange={handleChange}
                    className="form-control"
                    placeholder="Enter staff name"
                    disabled={loading}
                  />
                </div>

                {/* Email Field */}
                <div className="mb-3">
                  <label className="form-label">Email</label>
                  <input
                    type="email"
                    name="email"
                    value={form.email}
                    onChange={handleChange}
                    className="form-control"
                    placeholder="Enter staff email"
                    disabled={loading}
                  />
                </div>

                {/* Role Select Box */}
                <div className="mb-4">
                  <label className="form-label">Role</label>
                  <select
                    name="role"
                    value={form.role}
                    onChange={handleChange}
                    className="form-control"
                    disabled={loading}
                  >
                    <option value="">Select Role</option>
                    <option value="1">Admin</option>
                    <option value="2">Staff</option>
                  </select>
                </div>

                {/* Submit Button */}
                <button type="submit" className="btn btn-primary" disabled={loading}>
                  {loading ? (
                    <>
                      <span
                        className="spinner-border spinner-border-sm me-2"
                        role="status"
                        aria-hidden="true"
                      ></span>
                      Adding...
                    </>
                  ) : (
                    "Add Staff"
                  )}
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default AddStaff;

