import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { toast } from "react-toastify";
import { useMask } from "@react-input/mask";
import { countryCodes } from "../countryCodes";



function NewCustomerForm() {
  const navigate = useNavigate();
  const location = useLocation();
  const customerType = location.state?.customerType || "new";
  const [showDuplicateNotice, setShowDuplicateNotice] = useState(false);
  const [isChecked, setIsChecked] = useState(true);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;

  const cellPhoneRef = useMask({
    mask: "(___) ___-____",
    replacement: { _: /\d/ },
  });

  const stripMask = (val) => (val ? val.replace(/\D/g, "") : ""); // remove formatting

  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  // Add this function to handle country selection
  const handleCountrySelect = (code) => {
    setFormData((prev) => ({
      ...prev,
      country_code: code,
    }));
    setIsDropdownOpen(false);
  };

  const [formData, setFormData] = useState({
    first_name: "",
    last_name: "",
    dob: "",
    address: "",
    city: "",
    province: "",
    postal_code: "",
    cell_phone: "",
    email: "",
    signing_for_minor: false,
    minors: [],
    country_code: "+1"
  });  

  const [minorList, setMinorList] = useState([]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleMinorChange = (index, field, value) => {
    const updated = [...minorList];
    updated[index][field] = value;
    setMinorList(updated);
  };

  const addMinor = () => {
    setMinorList([...minorList, { first_name: "", last_name: "", dob: "" }]);
  };

  const removeMinor = (index) => {
    const updated = [...minorList];
    updated.splice(index, 1);
    setMinorList(updated);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    // const fullData = { ...formData, minors: minorList, send_otp: isChecked };


    const phoneWithCode = `${formData.country_code}${stripMask(formData.cell_phone)}`;
    const fullData = {
      ...formData,
      cc_cell_phone: phoneWithCode,
      minors: minorList,
      send_otp: isChecked,
    };

    try {
      const response = await axios.post(`${BACKEND_URL}/api/waivers`, fullData);
      if (isChecked) {
        toast.success(`Customer created and OTP sent successfully.`);
        navigate("/opt-verified", {
          state: { phone: stripMask(formData.cell_phone), customerType: "new" },
        });
      } else {
        toast.success("Customer created successfully. Skipping OTP.");
        navigate("/signature", {
          state: { phone: stripMask(formData.cell_phone) },
        });
      }
    } catch (err) {
      if (err.response && err.response.status === 409) {
        toast.error(
          "🚫 This phone number already exists. Please use a different number."
        );
        setShowDuplicateNotice(true);
      } else if (err.response && err.response.data?.error) {
        toast.error(`❌ ${err.response.data.error}`);
      } else {
        console.error(err);
        toast.error("❌ Error submitting form. Please try again.");
      }
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleNextClick = () => {
    navigate("/existing-customer", {
      state: { phone: stripMask(formData.cell_phone) },
    });
  };

  return (
    <div className="container-fluid">
      <div className="container text-center">
        <div className="row">
          <div className="col-md-2">
            <div className="back-btn">
              <Link to="/">
                <img
                  className="img-fluid"
                  src="/assets/img/image 298.png"
                  alt="back-icon"
                />{" "}
                BACK
              </Link>
            </div>
          </div>

          <div className="col-12 col-md-8 col-xl-8">
            <div className="step-two step-three">
              <div className="logo">
                <img
                  className="img-fluid"
                  src="/assets/img/SKATE_AND_PLAY_V08_Full_Transparency (2) 1.png"
                  alt="logo"
                />
              </div>
            </div>
          </div>
        </div>

        <div className="row">
          <div className="col-md-12 col-xl-10 mx-auto">
            <h3 className="h5-heading">Your details </h3>
            <form onSubmit={handleSubmit}>
              <div className="info-table w-100">
                <table cellPadding="8" cellSpacing="0">
                  <tbody>
                    <tr>
                      <td>
                        First Name:<span className="required-star">*</span>
                        <br />
                        <input
                          type="text"
                          name="first_name"
                          value={formData.first_name}
                          onChange={handleChange}
                          className="form-control"
                          required
                        />
                      </td>
                      <td>
                        Last Name:<span className="required-star">*</span>
                        <br />
                        <input
                          type="text"
                          name="last_name"
                          value={formData.last_name}
                          onChange={handleChange}
                          className="form-control"
                          required
                        />
                      </td>
                      <td>
                        DOB:<span className="required-star">*</span>
                        <br />
                        <input
                          type="date"
                          name="dob"
                          value={formData.dob}
                          onChange={handleChange}
                          className="form-control"
                          required
                        />
                      </td>
                    </tr>

                    <tr>
                      <td >
                        Address:<span className="required-star">*</span>
                        <br />
                        <input
                          type="text"
                          name="address"
                          value={formData.address}
                          onChange={handleChange}
                          className="form-control"
                          required
                        />
                      </td>
                      <td>
                        City:<span className="required-star">*</span>
                        <br />
                        <input
                          type="text"
                          name="city"
                          value={formData.city}
                          onChange={handleChange}
                          className="form-control"
                          required
                        />
                      </td>
                      <td>
                        Province:<span className="required-star">*</span>
                        <br />
                        <input
                          type="text"
                          name="province"
                          value={formData.province}
                          onChange={handleChange}
                          className="form-control"
                          required
                        />
                      </td>

                    </tr>

                    <tr>

                          <td>
                        Postal Code:<span className="required-star">*</span>
                        <br />
                        <input
                          type="text"
                          name="postal_code"
                          value={formData.postal_code}
                          onChange={handleChange}
                          className="form-control"
                          required
                        />
                      </td>
                      {/* <td>
                        Cell Phone:<span className="required-star">*</span>
                        <br />
                        <input
                          ref={cellPhoneRef}
                          type="tel"
                          name="cell_phone"
                          value={formData.cell_phone}
                          onChange={handleChange}
                          className="form-control"
                          required
                        />
                      </td> */}

                      <td>
                        Cell Phone:<span className="required-star">*</span>
                        <br />
                        <div className="phone-input-group">
                          <div className="custom-dropdown" style={{ position: 'relative' }}>
                            <div
                              className="form-select"
                              onClick={() => setIsDropdownOpen(!isDropdownOpen)}
                              style={{ cursor: 'pointer' }}
                            >
                              {formData.country_code}
                            </div>
                            {isDropdownOpen && (
                              <div
                                className="dropdown-menu show"
                                style={{
                                  position: 'absolute',                              
                                  maxHeight: '200px',
                                  overflowY: 'auto',
                                  zIndex: 1000,
                                }}
                              >
                                {countryCodes.map((c, index) => (
                                  <div
                                    key={index}
                                    className="dropdown-item"
                                    onClick={() => handleCountrySelect(c.code)}
                                    style={{ cursor: 'pointer' }}
                                  >
                                    {c.code} - {c.name}
                                  </div>
                                ))}
                              </div>
                            )}
                          </div>

                          <input
                            ref={cellPhoneRef}
                            type="tel"
                            name="cell_phone"
                            value={formData.cell_phone}
                            onChange={handleChange}
                            className="form-control"
                            required
                          />
                        </div>
                      </td>

                      <td >
                        Email:<span className="required-star">*</span>
                        <br />
                        <input
                          type="email"
                          name="email"
                          value={formData.email}
                          onChange={handleChange}
                          className="form-control"
                          required
                        />
                      </td>
                    </tr>
                  </tbody>
                </table>
              </div>

              <div className="text-start my-4">
                <div className="d-flex align-items-center custom-radio-wrapper">
                  <h5>I'm signing on behalf of a minor or dependent</h5>
                  <label className="ms-3">
                    <input
                      type="radio"
                      name="signing_for_minor"
                      checked={formData.signing_for_minor === true}
                      onChange={() => {
                        setFormData((p) => ({ ...p, signing_for_minor: true }));
                        if (minorList.length === 0) {
                          setMinorList([
                            { first_name: "", last_name: "", dob: "" },
                          ]);
                        }
                      }}
                    />{" "}
                    Yes
                  </label>
                  <label className="ms-3">
                    <input
                      type="radio"
                      name="signing_for_minor"
                      checked={formData.signing_for_minor === false}
                      onChange={() => {
                        setFormData((p) => ({
                          ...p,
                          signing_for_minor: false,
                        }));
                        setMinorList([]);
                      }}
                    />{" "}
                    No
                  </label>
                </div>
              </div>

              {formData.signing_for_minor && (
                <>
                  {minorList.map((minor, index) => (
                    <div key={index} className="minor-group d-flex gap-2 mb-3">
                      <input
                        type="text"
                        className="form-control"
                        placeholder="First Name"
                        value={minor.first_name}
                        onChange={(e) =>
                          handleMinorChange(index, "first_name", e.target.value)
                        }
                      />
                      <input
                        type="text"
                        className="form-control"
                        placeholder="Last Name"
                        value={minor.last_name}
                        onChange={(e) =>
                          handleMinorChange(index, "last_name", e.target.value)
                        }
                      />
                      <input
                        type="date"
                        className="form-control"
                        value={minor.dob}
                        onChange={(e) =>
                          handleMinorChange(index, "dob", e.target.value)
                        }
                      />
                      <button
                        type="button"
                        className="btn btn-danger"
                        onClick={() => removeMinor(index)}
                      >
                        Remove
                      </button>
                    </div>
                  ))}
                  <button
                    type="button"
                    onClick={addMinor}
                    className="btn btn-primary my-2"
                  >
                    Add another minor
                  </button>
                </>
              )}

              <div className="my-4">
                <div className="confirm-box text-start">
                  <label className="custom-checkbox-wrapper">
                    <input
                      type="checkbox"
                      className="custom-checkbox"
                      checked={isChecked}
                      onChange={() => setIsChecked((prev) => !prev)}
                    />
                    <span className="custom-checkbox-label">
                      <h5>
                        Save time on your next visit! Use your phone number as a
                        reference for future waivers. Just check the box and
                        receive a quick validation text.
                      </h5>
                    </span>
                  </label>
                </div>
              </div>

              <div className="buttons mb-5">
                {showDuplicateNotice && (
                  <div className="alert alert-warning w-50 mx-auto text-center">
                    This phone number already exists. Please proceed as an
                    existing user. <br />
                    If you want to log in,{" "}
                    <span
                      onClick={handleNextClick}
                      style={{
                        textDecoration: "underline",
                        cursor: "pointer",
                        color: "#0d6efd",
                      }}
                    >
                      click here
                    </span>
                    .
                  </div>
                )}
                <button
                  type="submit"
                  className="btn btn-primary w-25"
                  disabled={isSubmitting}
                >
                  {isSubmitting ? (
                    <>
                      <span
                        className="spinner-border spinner-border-sm me-2"
                        role="status"
                        aria-hidden="true"
                      ></span>
                      Submitting...
                    </>
                  ) : (
                    "Next"
                  )}
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default NewCustomerForm;

src/pages/ConfirmCustomerInfo.js.content:
import React, { useEffect, useState } from "react";
import { useLocation, useNavigate , Link} from "react-router-dom";
import axios from "axios";
import { toast } from 'react-toastify';
import { useMask } from "@react-input/mask"; // ✅ Import mask

function ConfirmCustomerInfo() {
  const location = useLocation();
  const navigate = useNavigate();
  const phone = location.state?.phone;
  const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;

  const customerType = location.state?.customerType || "existing";

  const [formData, setFormData] = useState(null);
  const [minorList, setMinorList] = useState([]);
   // ✅ Phone mask refs
  const homePhoneRef = useMask({ mask: "(___) ___-____", replacement: { _: /\d/ } });
  const cellPhoneRef = useMask({ mask: "(___) ___-____", replacement: { _: /\d/ } });
  const workPhoneRef = useMask({ mask: "(___) ___-____", replacement: { _: /\d/ } });


  useEffect(() => {
    if (phone) {
      axios
        .get(`${BACKEND_URL}/api/waivers/customer-info?phone=${phone}`)
        .then((res) => {
          const data = res.data.customer;


            // ✅ Convert numbers into masked format if exists
          const formatPhone = (num) => {
            if (!num) return "";
            const digits = num.replace(/\D/g, "").slice(0, 10);
            if (digits.length === 10) {
              return `(${digits.slice(0, 3)}) ${digits.slice(3, 6)}-${digits.slice(6)}`;
            }
            return digits;
          };

          data.home_phone = formatPhone(data.home_phone);
          data.cell_phone = formatPhone(data.cell_phone);
          data.work_phone = formatPhone(data.work_phone);


          data.can_email = data.can_email === 1 || data.can_email === "1";
          setFormData(data);

          if (res.data.minors) {
            const minorsWithFlags = res.data.minors.map((minor) => ({
              ...minor,
              dob: minor.dob ? new Date(minor.dob).toISOString().split("T")[0] : "",
              checked: minor.status === 1,
              isNew: false,
            }));
            setMinorList(minorsWithFlags);
          }
        })
        .catch((err) => {
          console.error(err);
          // alert("Failed to fetch customer info.");
           toast.error(err?.response?.data?.message || "Failed to fetch customer info.");
        });
    }
  }, [phone, BACKEND_URL]);

  // const handleChange = (e) => {
  //   const { name, value, type } = e.target;
  //   setFormData((prev) => ({
  //     ...prev,
  //     [name]: type === "number" ? Number(value) : value,
  //   }));
  // };

   const handleChange = (e) => {
    const { name, value } = e.target;

    // ✅ If it's a phone → only store masked for UI
    if ([ "cell_phone"].includes(name)) {
      setFormData((prev) => ({
        ...prev,
        [name]: value,
      }));
    } else {
      setFormData((prev) => ({
        ...prev,
        [name]: value,
      }));
    }
  };

  const goToSignature = async () => {
    try {
       const stripMask = (val) => (val ? val.replace(/\D/g, "") : ""); // ✅ remove formatting
      const updatedData = {
        ...formData,

        cell_phone: stripMask(formData.cell_phone),

        minors: minorList.map((minor) => ({
          id: minor.id,
          first_name: minor.first_name,
          last_name: minor.last_name,
          dob: minor.dob,
          isNew: minor.isNew,
          checked: minor.checked,
        })),
      };

      await axios.post(`${BACKEND_URL}/api/waivers/update-customer`, updatedData);

      navigate("/signature", { state: { phone, formData: updatedData } });
    } catch (err) {
      console.error("Error updating customer:", err);
      // alert("Failed to update customer info.");
        toast.error("Failed to update customer info.");
    }
  };
  if (!formData) {
    return <div className="text-center mt-5">Loading customer info...</div>;
  }

  return (
    <div className="container-fluid">
      <div className="container text-center">
        <div className="row">
          <div className="col-md-2">
            <div className="back-btn">
              <Link to="/existing-customer">
                <img className="img-fluid" src="/assets/img/image 298.png" alt="back-icon" /> BACK
              </Link>
            </div>
          </div>
          <div className="col-12 col-md-8 col-xl-8">
            <div className="step-two step-three">
              <div className="logo">
                <img className="img-fluid" src="/assets/img/SKATE_AND_PLAY_V08_Full_Transparency (2) 1.png" alt="logo" />
              </div>
            </div>
          </div>
        </div>

        <div className="row">
          <div className="col-md-12 col-xl-10 mx-auto">
            <h3 className="h5-heading">Please confirm that all information below is still accurate</h3>

            <form>
              <div className="info-table w-100">
                <table cellPadding="8" cellSpacing="0">
                  <tbody>
                    <tr>
                      <td>
                        Participant First Name:<br />
                        <input type="text" name="first_name" value={formData.first_name || ""} onChange={handleChange} className="form-control" readOnly/>
                      </td>
                      <td>
                        Participant Last Name:<br />
                        <input type="text" name="last_name" value={formData.last_name || ""} onChange={handleChange} className="form-control" readOnly />
                      </td>

                      <td>
                        Date of Birth:<br />
                        <input type="date" name="dob" value={formData.dob || ""} onChange={handleChange} className="form-control" readOnly/>
                      </td>

                    </tr>
                    <tr>
                      <td>
                        Address:<br />
                        <input type="text" name="address" value={formData.address || ""} onChange={handleChange} className="form-control" readOnly/>
                      </td>
                      <td>
                        City:<br />
                        <input type="text" name="city" value={formData.city || ""} onChange={handleChange} className="form-control" readOnly/>
                      </td>
                      <td>
                        Province:<br />
                        <input type="text" name="province" value={formData.province || ""} onChange={handleChange} className="form-control" readOnly/>
                      </td>

                    </tr>
                    <tr>

                      <td>
                        Postal Code:<br />
                        <input type="text" name="postal_code" value={formData.postal_code || ""} onChange={handleChange} className="form-control" readOnly/>
                      </td>
                      {/* <td>
                        Cell Phone:<br />
                        <input
                          ref={cellPhoneRef}
                          type="tel"
                          name="cell_phone"
                          value={`${formData.country_code} ${formData.cell_phone}` || ""}
                          onChange={handleChange}
                          className="form-control"
                          readOnly
                        />
                      </td> */}

                      <td>
                        Cell Phone:<br />
                        <input
                          type="tel"
                          name="cell_phone"
                          value={
                            formData.country_code && formData.cell_phone
                              ? `${formData.country_code} ${formData.cell_phone}`.trim()
                              : formData.cell_phone || ""
                          }
                          className="form-control"
                          readOnly
                        />
                      </td>

                      <td>
                        Email:<br />
                        <input type="email" name="email" value={formData.email || ""} onChange={handleChange} className="form-control" readOnly/>
                      </td>

                    </tr>
                  </tbody>
                </table>
              </div>

              {/* ✅ Minor Section */}
              <div className="minor-section my-4 text-start">
                <h5>Please check mark to sign on behalf of the below minor or dependent</h5>

                {minorList.map((minor, index) => (
                  <div key={index} className="minor-group d-flex align-items-center gap-2 my-2">
                    <label className="custom-checkbox-wrapper">
                      <input
                        type="checkbox"
                        className="custom-checkbox"
                      checked={minor.checked}
                        onChange={(e) => {
                          const updated = [...minorList];
                          updated[index].checked = e.target.checked;
                          setMinorList(updated);
                        }}
                      />
                    </label>
                    <input
                      type="text"
                      className="form-control"
                      placeholder="First Name"
                      value={minor.first_name}
                      onChange={(e) => {
                        const updated = [...minorList];
                        updated[index].first_name = e.target.value;
                        setMinorList(updated);
                      }}
                    />
                    <input
                      type="text"
                      className="form-control"
                        placeholder="Last Name"
                      value={minor.last_name}
                      onChange={(e) => {
                        const updated = [...minorList];
                        updated[index].last_name = e.target.value;
                        setMinorList(updated);
                      }}
                    />
                    <input
                      type="date"
                      className="form-control"
                      value={minor.dob}
                      onChange={(e) => {
                        const updated = [...minorList];
                        updated[index].dob = e.target.value;
                        setMinorList(updated);
                      }}
                    />
                    {minor.isNew && (
                      <button
                        type="button"
                        className="btn btn-danger"
                        onClick={() => {
                          const updated = [...minorList];
                          updated.splice(index, 1);
                          setMinorList(updated);
                        }}
                      >
                        Remove
                      </button>
                    )}
                  </div>
                ))}

                <div className="buttons mt-3">
                  <button
                    type="button"
                    className="btn btn-secondary me-2"
                    onClick={() =>
                      setMinorList([
                        ...minorList,
                        { first_name: "", last_name: "", dob: "", checked: true, isNew: true },
                      ])
                    }
                  >
                    Add another minor
                  </button>
                  <span className="or-text mx-2">or</span>
                  <button type="button" className="btn btn-primary" onClick={goToSignature}>
                    Confirm
                  </button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ConfirmCustomerInfo;

src/pages/admin/ResetPassword.js.content:
import React, { useEffect, useState } from "react";
import { useSearchParams, useNavigate } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";

function ResetPasswordForm() {
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();

  const [form, setForm] = useState({
    password: "",
    confirmPassword: "",
  });

  const [email, setEmail] = useState("");
  const [id, setId] = useState("");

  useEffect(() => {
    const encodedEmail = searchParams.get("email");
    const encodedId = searchParams.get("id");

    if (encodedEmail && encodedId) {
      setEmail(atob(encodedEmail));
      setId(atob(encodedId));
    } else {
      toast.error("Invalid reset link.");
    }
  }, [searchParams]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || "http://localhost:5000";
      const response = await axios.post(`${BACKEND_URL}/api/staff/update-password`, {
        email,
        id,
        password: form.password,
        confirmPassword: form.confirmPassword,
      });


        // ✅ Auto-login: store token and user info
    localStorage.setItem("token", response.data.token);
    localStorage.setItem("staff", JSON.stringify(response.data.staff));

      toast.success(response.data || "Password updated successfully!");

    navigate("/admin/home"); // Redirect to home after login
    } catch (err) {
      toast.error(err.response?.data || "Error resetting password");
    }
  };

  return (
        <div className="container-fluid">
      <div className="container">
        <div className="row">
          <div className="col-12 col-md-6 col-xl-4 mx-auto">
            <div className="login-container">
              <div className="logo text-center mb-4">
                <img
                  className="img-fluid"
                  src="/assets/img/SKATE_AND_PLAY_V08_Full_Transparency (2) 1.png"
                  alt="logo"
                />
              </div>
      <form className="mt-4" onSubmit={handleSubmit}>
        <div className="mb-3">
          <label>New Password</label>
          <input
            type="password"
            className="form-control"
            value={form.password}
            onChange={(e) => setForm({ ...form, password: e.target.value })}
            required
          />
        </div>
        <div className="mb-3">
          <label>Confirm Password</label>
          <input
            type="password"
            className="form-control"
            value={form.confirmPassword}
            onChange={(e) => setForm({ ...form, confirmPassword: e.target.value })}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary w-100">Update Password</button>
      </form>
          </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ResetPasswordForm;
